export function getProductByCategory(id) {
    console.log(id);
  return dispatch => {
     callApi(id,data => {
         dispatch(setData(data));//tra ve cho form
        
    });
  }
}

function setData(data) {
  return {
    type:GET_PRODUCT_BY_CATEGORY,
   data
  };
}

function callApi( id,callback)
{  var sub_id=null;
  axios.get(`http://127.0.0.1:3000/categories?parent_id=`+id)
  .then(res => {
     if (res.data.length>0)
     { console.log(res.data);
        let cate=res.data;
        sub_id=cate[1].id;
        axios.get(`http://127.0.0.1:3000/products?category_id=`+sub_id)
       .then(res =>
         {
          if (res.data!=null)
          {callback(res.data);
           } 
         })

    } 
    else
     {
    axios.get(`http://127.0.0.1:3000/products?category_id=`+id)
    .then(res => {
       if (res.data!=null)
       {callback(res.data);
       
      } 
    }
    )
    }
})
  

}
export function getCategoryName(id) {
  console.log(id);
return dispatch => {
   Api(id,data => {
       dispatch(set(data));//tra ve cho form
      
  });
}
}

function set(data) {
return {
  type:GET_CATEGORY_NAME,
  data
};
}

function Api( id,callback)
{  
  axios.get(`http://127.0.0.1:3000/categories?id=`+id)
  .then(res => {
     if (res.data!=null)
     {  console.log(res.data[0].category_name);
       callback(res.data[0].category_name);
     
    } 
  }
  )


}
var dt = JSON.parse(localStorage.getItem('ProductByCategory'));
var dt1=localStorage.getItem('CategoryName');
var initialState = 
{ data:dt ? dt :[],
  categoryname:dt1?dt1:null,
}

export default function menu_reducer(state =initialState, action) {
  if(action.type=='GET_PRODUCT_BY_CATEGORY')
  {  
    let newState={...state};
    newState.data=action.data
    localStorage.setItem('ProductByCategory', JSON.stringify(newState.data));
    return newState;
  }
  if(action.type=='GET_CATEGORY_NAME')
  {  
    let newState={...state};
    newState.categoryname=action.categoryname
    localStorage.setItem('ProductByCategory',newState.categoryname);
    return newState;
  }
  else return state;
}
