import axios from 'axios';  
const GET_PRODUCT='GET_PRODUCT';

  export function getProduct(id) {
    return dispatch => {
       callApi(id,data => {
        dispatch(set(data));
        //tra ve cho form
          
      });
    }
  }


  function set(product) {
    console.log("check xem co sp o day chua");
    console.log(product);
    return {
      type: GET_PRODUCT,
      product
    };
  }


function  callApi(id,callback) {
  axios.get(`http://127.0.0.1:3000/order_detail?order_id=`+id)
  .then(res => {
  const  orders = res.data;
    if (res.data!=null)
     { console.log(orders);
       var list_product=[];
       console.log(orders.length);
       if (orders.length > 0) {
        for (var i = 0; i < orders.length; i++) {
          axios.get(`http://127.0.0.1:3000/products?id=`+orders[i].product_id)
          .then(res => {
             list_product.push(res.data);
          })
        }
        return callback(list_product)
    }
     } 
  }
  )

}

var data=JSON.parse(localStorage.getItem('productorderdetail'));
var initialState = data ? data : [];
export default function product_order_detail_reducer(state =initialState, action) {
 if(action.type=='GET_PRODUCT')
 {   localStorage.setItem('productorderdetail', JSON.stringify(action.product));
     let newState={...state};
     newState.product=action.product;
     localStorage.setItem('productorderdetail', JSON.stringify(action.product));
     return newState;
     
 }
 
 else return state;
}
