import React, { Component } from 'react';
import { Link ,Redirect} from "react-router-dom";
import { connect } from 'react-redux';
class Search extends Component {
  constructor(props)
  {
    super(props);
    this.state = {};
  }
    render() {
        let { list_product_get_by_name} = this.props;
        console.log(this.props);
        return (
                <div>
                    <div class="category-featured digital">
                     <nav class="navbar nav-menu result-brand">
                      <div class="navbar-brand">
                      <span>
                      Kết Quả Tìm Kiếm
                    </span>
                
                   </div>
                </nav>
               <div class="product-featured clearfix">
               <div class="row"></div>
               <div class="col-sm-12 category-product"></div>
                 {
                  this.props.list_product_get_by_name.map(function(item, i){
                      return(
                
                        <div class="col-sm-4">
                        <div class="product-item">
                          <div class="pi-img-wrapper">
                          <img  style={style} src={require(item.product_image)} alt=""/>
                            <div>
                             <Link to='./login'>Detail of product</Link>
                            </div>
                          </div>
                          <h3>
                          <Link to='./login'>{item.product_name}</Link>
                          </h3>
                          <div class="pi-price">
                           Price:{item.price}
                          </div>
                          
                            <div class="sticker sticker-new">
                          </div>
                            <div>
                         
                            <button type="button" class="btn btn-primary">Add into Cart</button>
                            
                            </div>
                        </div>
                      </div>


                      )
                 })
                }
                 </div>
               </div>
   
            </div>
          

                
            );
}   
}            
const mapStateToProps = (state) => {//tra state return ve tu reducer ve thanh prop
    return {
      list_product_get_by_name:state.searchState.allProduct,
    };
    }
  export default connect(mapStateToProps,null)(Search);
