import React, { Component } from 'react';

class CartResult extends Component {
    constructor(props)
    {
        super(props);
        this.Orders=this.Orders.bind(this);
    }
    render() {
        var { cart } = this.props;
        var {cartOrder,totalPayment}=this.state;
        return (
            <tr>
                <td colSpan="3"></td>
                <td>
                    <h4>
                        <strong>Tổng Tiền</strong>
                    </h4>
                </td>
                <td>
                    <h4>
                        <strong  onChange={e => this.setState({totalPayment:this.showTotalAmount(cart),cartOrder:this.cart})}>{this.showTotalAmount(cart)}Đ</strong>
                    </h4>
                </td>
                <td colSpan="3">

                    <button type="button" onClick={this.Orders} className="btn btn-primary waves-effect waves-light" style={{backgroundColor:"#A52A2A"}}>Order

                    </button>
                </td>
            </tr>
        );
    }

    showTotalAmount = (cart) => {
        var total = 0;
        if (cart.length > 0) {
            for (var i = 0; i < cart.length; i++) {
                total += cart[i].product.price * cart[i].quantity;
            }
        }
        return total;
    }
    Orders(e)
    {   e.preventDefault();
        let { cartOrder, totalPayment } = this.state;//lay trang thai off state do qua email va password
        this.props.setOrder(cartOrder,totalPayment);//tao 1 props goi la login(email,password)

    }

}
const mapDispatchToProps = (dispatch) => {//store.dispatch(action)
    return {
     setOrder:(cartOrder,totalPayment)=> dispatch(setOrder(cartOrder,totalPayment)),

      //action la login voi 2 tham so la email va password
    };
    }
export default CartResult;
