import axios from "axios";
import config from "./configs";
import headerHeper from "./HeaderHelper";

let APICaller = (endpoint, method = 'GET', body) => {
    return axios({
        method: method,
        url: `${config.API_URL}/${endpoint}`,
        data: body,
        headers: headerHeper()
    });
};

export default APICaller;
