import React, { Component } from 'react';
import { connect } from 'react-redux';
import './Detail.css';
class Detail extends Component {
  
    constructor(props) {
        super(props);
        this.state = {};
       // this.onSubmit = this.onSubmit.bind(this);
      }
      componentWillMount()
      {
      
      }
render()
{  let {product} =this.props;
  let {isLogingSuccess}=this.state;   
  console.log("hdjsd");
  console.log(this.props);
  console.log(product.length);
    return (
        <div>
         
          {
            product.length >0 && <div>
               
              <div className="row product-details">
        <nav className="navbar nav-menu details-brand">
         <div className="navbar-brand">
    Product Detail
    </div>
  </nav>
  <div className="card">
    <div className="container-fliud">
      <div className="wrapper row">
        <div className="preview col-md-6">
          <div className="preview-pic tab-content">
            <div className="tab-pane active" id="pic-1">
            <img className="img-responsive"  src={require('../../assets/'+product[0].product_image)} alt=""/>
            </div>
          </div>
          <ul className="preview-thumbnail nav nav-tabs">
            <li className="active">
              <a data-target="#pic-1" data-toggle="tab">
              <img className="img-responsive"  src={require('../../assets/'+product[0].product_image)} alt=""/>
              </a>
            </li>
            <li className="active">
              <a data-target="#pic-2" data-toggle="tab">
              <img className="img-responsive"  src={require('../../assets/'+product[0].product_image)} alt=""/>
              </a>
            </li>
            <li className="active">
              <a data-target="#pic-3" data-toggle="tab">
              <img className="img-responsive"  src={require('../../assets/'+product[0].product_image)} alt=""/>
              </a>
            </li>
            <li className="active">
              <a data-target="#pic-4" data-toggle="tab">
              <img className="img-responsive"  src={require('../../assets/'+product[0].product_image)} alt=""/>
              </a>
            </li>
            <li className="active">
              <a data-target="#pic-4" data-toggle="tab">
              <img className="img-responsive"  src={require('../../assets/'+product[0].product_image)} alt=""/>
              </a>
            </li>
          </ul>
        </div>
        <div className="details col-md-6">
          <h3 className="product-title">{product[0].product_name}</h3>
         
          
          <p className="product-description">
            {product[0].product_details}
          </p>
       
          <div className="pi-price">
                           Price:{product[0].price} Đ
                          </div>
          <br/>              
          <div className="pi-quantity"> 
          <span>Quantity:</span><span> <input type="number" name="quantity" min="1" max={product[0].quantity} /></span>
          </div> 
          <div className="sticker sticker-new">
           </div>
           <div>
          <button style={{marginTop:"20px"}}className="btn add2cart"> Add into cart</button>
          {/*<div className="fb-comments" data-href="https://developers.facebook.com/docs/plugins/comments#configurator" data-width="" data-numposts="5"></div>*/}
          </div>
        

        </div>
      </div>
    </div>
  </div>
  </div>
   </div>  
        }
     
        </div>

      )
  }
}   
const mapStateToProps = (state) => {//tra state return ve tu reducer ve thanh prop
    var a=false;
    if(localStorage.getItem('token')=="abcdefghiklm") a=true;
        return {
            product:state.productDetailState.Product,
            isLogingSuccess:a
        };
        }
    
export default connect(mapStateToProps,null)(Detail);
