import React from 'react';
import './Signup.css';
import { Link } from "react-router-dom";
import { connect } from 'react-redux';
import { signup } from '../../redux/signup_reducer';

class Signup extends React.Component{
  constructor(props){
    super(props);
    this.state={};
    this.onSubmit = this.onSubmit.bind(this);

  }
 
 
 render(){
  let {user_name,email,password,password_confirm,address}= this.state;//state co 2 trang thai la email va password
  let {isSignupPending, isSignupSuccess, signupError} = this.props;
  if(isSignupSuccess) alert("signup success");
  return (
    <div>
       <title>Sign Up</title>
      <center>
    <div className="signup_div">
    <p id="header_para" className="w3-animate-fading"><b>Style</b><span id="card">Shop</span></p>
    <hr id="hr1" />&nbsp;
    <hr id="hr2" /><br /><br /><br />
    <form action>
      <label>Username:</label> <input type="text" style={{marginLeft:"20px"}}name="username" placeholder="username" required className="input_style w3-animate-left" onChange={e => this.setState({user_name: e.target.value})} value={user_name} /><br /><br />
       <label>Email:</label> <input type="email" style={{marginLeft:"20px"}}name="email" placeholder="username" required className="input_style w3-animate-right"  onChange={e => this.setState({email: e.target.value})} value={email} /><br /><br />
        <label>Password:</label><input type="password" style={{marginLeft:"20px"}}name="password" placeholder="password" required className="input_style w3-animate-left" onChange={e => this.setState({password: e.target.value})} value={password} /><br /><br />
       <label> password_confirm:</label><input type="password"style={{marginLeft:"20px"}} name="password" placeholder="password confirm" required className="input_style w3-animate-left" onChange={e => this.setState({password_confirm: e.target.value})} value={password_confirm} /><br /><br />
       <label> Address:</label><input type="text"style={{marginLeft:"20px"}} name="address" placeholder="Address" required className="input_style w3-animate-left" onChange={e => this.setState({address: e.target.value})} value={address} /><br /><br />
        <button name="submit_button" id="signup_button" onClick={this.onSubmit}className="w3-animate-bottom" ><Link to="/home">Signup</Link></button>
    </form>
    <p id="lg" className="w3-animate-zoom"><Link to="./login">Login Now!!</Link ></p><br /><p />
    </div>
      </center>
     
             
             
          <div className="message">
          { isSignupPending && <div>Please wait...</div> }
          { isSignupSuccess && <div>Success.</div> }
          { signupError && <div>{signupError.message}</div> }
        </div>
      
     
    </div>
  
       
  );
  }

onSubmit(e) {
  e.preventDefault();
  let { user_name,email, password,password_confirm,address } = this.state;//lay trang thai off state do qua email va password
  this.props.signup(user_name,email,password,password_confirm ,address);
  this.setState({
    user_name:'',
    email: '',
    password: '',
    password_confirm:'',
    address:''
  });
}
}

const mapStateToProps = (state) => {//tra state return ve tu reducer ve thanh prop
return {
  isSignupPending: state.signupState.isSignupPending,
  isSignupSuccess: state.signupState.isSignupSuccess,
  signupError: state.signupState.signupError
};
}

const mapDispatchToProps = (dispatch) => {//store.dispatch(action)
return {
  signup: (user_name,email,password,password_confirm,address) => dispatch(signup(user_name,email, password,password_confirm,address))//action la login voi 2 tham so la email va password
};
}
export default connect(mapStateToProps, mapDispatchToProps)(Signup);
