//Login
import axios from 'axios';
const SET_LOGIN_PENDING = 'SET_LOGIN_PENDING';
const SET_LOGIN_SUCCESS = 'SET_LOGIN_SUCCESS';
const SET_LOGIN_ERROR = 'SET_LOGIN_ERROR';
const SET_LOGOUT_SUCCESS='SET_LOGOUT_SUCCESS';
const SET_CURRENT_USER='SET_CURRENT_USER';
export function login(email, password) { //reducer thuc hien action login xu ly du lieu lay qua
  return dispatch => {
    dispatch(setLoginPending(true));
    dispatch(setLoginSuccess(false));
    dispatch(setLoginError(null));

    callLoginApi(email, password, error => {
      dispatch(setLoginPending(false));//tra ve cho form
      if (!error) {
        dispatch(setLoginSuccess(true));//tra ve cho form
    
      } else {
        dispatch(setLoginError(error));//tra ve cho form
      }
    });
  }
}
export function logout() { //reducer thuc hien action login xu ly du lieu lay qua
  return dispatch => {
    localStorage.clear();
    dispatch(setLoginSuccess(false));
}
}
function setLoginPending(isLoginPending) {
  return {
    type: SET_LOGIN_PENDING,
    isLoginPending
  };
}

function setLoginSuccess(isLoginSuccess) {
  return {
    type: SET_LOGIN_SUCCESS,
    isLoginSuccess
  };
}
function setCurrrentUser(currentUser)
{    return {type:SET_CURRENT_USER,
            currentUser
           };
} 

function setLoginError(loginError) {
  return {
    type: SET_LOGIN_ERROR,
    loginError
  }
}

function callLoginApi(email, password, callback) {
 
  axios.get(`http://127.0.0.1:3000/users?email=`+email)
  .then(res => {
   
  const  user = res.data;
  console.log(user);
   setTimeout(() => {
    if (email === user[0].email&& password ===user[0].password) {
      localStorage.setItem('token','abcdefghiklm');
      
      return callback(null);//not error
    } else {
      return callback(new Error('Invalid email and password'));
    }
  }, 1000);
 

  }
  )

}
var login_state={
  isLoginPending:false,
  loginError:null,
  isLoginSuccess:false,
  currentUser:null,
}
export default function login_reducer(state =login_state, action) {
  if(action.type==' SET_LOGIN_PENDING')
  {
      let newState={...state};
      newState.isLoginPending=action.isLoginPending
      return newState;
  }
  if(action.type=='SET_LOGIN_SUCCESS')
  {
   
     let newState={...state};
     if(JSON.parse(localStorage.getItem('token'))=="abcdefghiklm")
     { newState.isLoginSuccess=true;
     }
      //action.isLoginSuccess;
     return newState;
    
       
     
  }
  if(action.type=='SET_LOGIN_ERROR')
  {  let newState={...state};
    newState.loginError=action.loginError;
    return newState;

  }
 
  if(action.type=='SET_CURRENT_USER')
  {
    let newState={...state};
    newState.currentUser=action.currentUser;
    return newState;
  }
      return state;
  
}
