                            </div >
                            
                            <p style={{fontSize:"32px",fontFamily:"'Times New Roman', Times, serif",marginTop:"30px"}}>Cửa hàng</p>
                            <div className="row">
                            <input className="form-control" type="text" placeholder="Tìm kiếm" style={{width:"170px",marginTop:"20px",float:"left"}}></input>
                            <button style={{width:"10%",marginTop:"20px",float:"left",marginRight:"50px",color:"black"}} onClick={e=>{this.props.getUserByName(this.state.key)}} className="form-control">Tìm</button>
