                                <table className="table table-striped" style={{fontSize:"20px",fontFamily:"Times New Roman', Times, serif"}}>
                            <thead>
                                <tr>
                                    <th>STT</th>
                                    <th>Tên cửa hàng</th>
                                    <th>Tên chủ cửa hàng</th>
                                    <th>Ngày thanh toán</th>
                                    <th>Ngày hết hạn</th>
                                    <th>Phí thanh toán</th>
                                    <th>Hành động</th>
                                </tr>
                            </thead>
                            <tbody>
                            {shops.length>0&&
                             shops.map((item,i)=>
                             {
                                 
        // "name": "Shop mỹ phẩm bà Kèo",
        // "user_name": "Bùi Thị Kiều",
        // "email": "buithikieu@gmail.com",
        // "phone": "0898237228",
        // "date_paid": "2019-12-08T14:46:36.5257021",
        // "date_expired": "2019-12-08T14:46:36.5272396",
