//Login
import axios from 'axios';
const GET_ALL_USER = 'GET_ALL_USER';
export function getAllUser() {
  console.log("abc"); 
  return dispatch => {
     callUserApi(data => {
         dispatch(setAllUser(data));//tra ve cho form
        
    });
  }
}



function setAllUser(allUser) {
  console.log(allUser);
  return {
    type: GET_ALL_USER,
    allUser
  };
}

function callUserApi( callback) {
  axios.get(`http://127.0.0.1:3000/users`)
  .then(res => {
  const  users = res.data;
    if (res.data!=null)
     {
      return callback(users);//not error
    } 
  }
  )

}
var user_state={
  allUser:[],

}
export default function user_reducer(state =user_state, action) {
  if(action.type=='GET_ALL_USER')
  { let newState={...state};
    newState.allUser=action.allUser
    return newState;
  }
  else return state;
}
