import React, { Component } from 'react';
import { connect } from 'react-redux';
import { login } from '../../redux/login_reducer';
import Home from '../Home/Home';
import './Login.css';
import { Redirect,Link} from "react-router-dom";

class Login extends React.Component  {
  constructor(props) {
    super(props);
    this.state = {};
    this.onSubmit = this.onSubmit.bind(this);
  }

  render(){
    console.log(this.props);
    let {email, password} = this.state;//state co 2 trang thai la email va password
    let {isLoginPending, isLoginSuccess, loginError} = this.props;
    if(isLoginSuccess) alert("Login success"); if(isLoginSuccess) alert("Login success");
      return (
    <div>
       <title>Log In</title>
      <center>
    <div className="login_div">
    <p id="header_para" className="w3-animate-fading"><b>Style</b><span id="card">Shop</span></p>
    <hr id="hr1" />&nbsp;
    <hr id="hr2" /><br /><br /><br />
    <form action>
        <input type="email" name="email" placeholder="username" required className="input_style w3-animate-right"  onChange={e => this.setState({email: e.target.value})} value={email} /><br /><br />
        <input type="password" name="password" placeholder="password" required className="input_style w3-animate-left" onChange={e => this.setState({password: e.target.value})} value={password} /><br /><br />
        <button name="submit_button" id="login_button" onClick={this.onSubmit}className="w3-animate-bottom" ><Link to="/home">Log In</Link></button>
    </form>
    <p id="forgot" className="w3-animate-zoom"><Link to="./signup">Sign Up Now!!</Link ></p><br /><p />
    </div>
      </center>
                   
<div className="message">
           {
             isLoginSuccess && <div>
              <Redirect to="/home "></Redirect>
             </div>
           }
           
         </div>
           </div>
        
        
       
          
       
  );
}
onSubmit(e) {
  e.preventDefault();
  let { email, password } = this.state;//lay trang thai off state do qua email va password
  this.props.login(email, password);//tao 1 props goi la login(email,password)
  this.setState({
    email: '',
    password: ''
  });
}
}

const mapStateToProps = (state) => {//tra state return ve tu reducer ve thanh prop
 
return {
  isLoginPending: state.loginState.isLoginPending,
  isLoginSuccess: state.loginState.isLoginSuccess,
  loginError: state.loginState.loginError
};
}

const mapDispatchToProps = (dispatch) => {//store.dispatch(action)
return {
  login: (email, password) => dispatch(login(email, password))//action la login voi 2 tham so la email va password
};
}

export default connect(mapStateToProps, mapDispatchToProps)(Login);
