import React, { Component } from 'react';
import { Link ,Redirect} from "react-router-dom";
import { connect } from 'react-redux';
import { show} from '../../redux/product_detail_reducer';
import './Recent.css';
import { actAddToCart} from '../../redux/cart_reducer';
import {addToRecentlyViewed} from '../../redux/recent_reducer';
class Recent extends Component {
  constructor(props)
  {
    super(props);
    this.state = {};
    //this.showDetail=this.showDetail.bind(this);
    this.onAddToCart=this.onAddToCart.bind(this);
  }
    render() {
        let { list_product_get_by_name,isLogingSuccess} = this.props;
        let { product_id,addProduct,quantity}=this.state;
        console.log(this)
        console.log(this.props);
        return (
                <div>
                    <div className="category-featured digital">
                     <nav className="navbar nav-menu result-brand">
                      <div className="navbar-brand">
                    
                     Sản phẩm gần đây
                  
                
                   </div>
                </nav>
               <div className="product-featured clearfix">
               <div className="row"></div>
               <div className="col-sm-12 category-product"></div>
              
                 {   
                  this.props.list_product_get_by_name.map((item, i)=>{
                    console.log(item.product);
                      return(
                       
                        <div className="col-sm-4">
                        <div className="product-item">
                          <div className="pi-img-wrapper">

                          <img className="img-responsive"  src={require('../../assets/'+item.product.product_image)} alt=""/>
                            <div>
                            <button className="btn" onClick={e =>{
                            this.props.show(item.product.id);
                            this.props.addToRecentlyViewed(item.product);
                            }
                            }><Link to='./product_detail'>Detail of Product</Link></button>
                            </div>
                          </div>
                          <h3>
                          <Link to='./product_detail'>{item.product.product_name}</Link>
                          </h3>
                          <div className="pi-price">
                            Price:{item.product.price} Đ
                           <span> <button type="submit" className="shoe-cart pshoe-cart" onClick={e=>{this.props.onAddToCart(item.product)}}style={{width:"20%"}}><i className="fa fa-cart-plus" aria-hidden="true" /></button></span>               
                          </div>
                          
                         
                        </div>
                      </div>


                      )
                 })
                }
              
                 </div>
               </div>
   
            </div>
          

                
            );
}   

onAddToCart(e)
  {  e.preventDefault();
    let { addProduct } = this.state;//lay trang thai off state do qua email va password
    this.props.onAddToCart(addProduct);
   }
} 

const mapDispatchToProps = (dispatch) => {//store.dispatch(action)
return {
  show:(product_id) => dispatch(show(product_id)),
  onAddToCart:(addProduct) => dispatch(actAddToCart(addProduct,1)),
  addToRecentlyViewed:(addProduct)=>dispatch(addToRecentlyViewed(addProduct)),
  //action la login voi 2 tham so la email va password
};
}     
const mapStateToProps = (state) => {//tra state return ve tu reducer ve thanh prop
  var a=false;
    if(localStorage.getItem('token')=="abcdefghiklm") a=true;
    return {
      list_product_get_by_name:state.recentState,
      isLogingSuccess:a
  };
    }

  export default connect(mapStateToProps,mapDispatchToProps)(Recent);
