import React, { Component } from 'react';
import { connect } from 'react-redux';
import {  getOrderHistory} from '../../redux/order_history_reducer';
class Orderhistory extends Component {
    constructor(props)
    {
        super(props);
        this.state={};


    }
    componentWillMount()
    {
        this.props.getOrders();
    }
    render()
    { let {list_order}=this.props;
        return (
   <div>     
<h1 class="admin-title">Lịch sử đặt hàng</h1>
<hr/>
<div class="admin-content">
  <div class="row">
    
  </div>
  <div class="row">
   {list_order.length>0 &&<div>
       {this.props.list_order.map((item,i)=>
       { return(
      <div>     
    <table className="table table-bordered">
      <tr>
       
        <th>No</th>
         <th>Username</th>
        <th>TotolPayment</th>
        <th>Detail</th>
        <th>Delete</th>
      </tr>
    
    </table>
    </div>
       )}
       )}
   </div>
   }
  </div>
</div>
</div>
    
        )}
}
const mapStateToProps = (state) => {//tra state return ve tu reducer ve thanh prop
    
      return {
          list_order:state.orderHistoryState
       
      };
    }
const mapDispatchToProps = (dispatch) => {//store.dispatch(action)
        return {
             getOrders:() =>dispatch(getOrderHistory())
           
      };
        }     
 export default connect(mapStateToProps,mapDispatchToProps)(Orderhistory);
