         dispatch(setData(data));//tra ve cho form
        
    });
  }
}

function setData(data) {
  console.log(data);
  return {
    type:GET_PRODUCT_BY_CATEGORY,
   data
  };
}

function callApi( id,callback) {
    var hasSub=false;
    var sub_id=null;
  axios.get(`http://127.0.0.1:3000/categories?parent_id=`+id)
  .then(res => {
     if (res.data!=null)
     { hasSub=true;
        let cate=res.data;
       sub_id=cate[1].id;
       console.log("sub_id");
       console.log(sub_id);
    } 
  }
  )
  if(hasSub==true)
  { axios.get(`http://127.0.0.1:3000/products?category_id=`+3)
  .then(res => {
    
     if (res.data!=null)
     {   console.log("product");
         console.log(res.data);
         callback(res.data);
     
    } 
  }
  )

  }
  else {
    axios.get(`http://127.0.0.1:3000/products?category_id=`+id)
    .then(res => {
       if (res.data!=null)
       {callback(res.data);
       
      } 
    }
    )
  }

}
var data=JSON.parse(localStorage.getItem('ProductByCategory'));
var products=data ?data:[];
var initialState={
 data:products,

}
export default function menu_reducer(state =initialState, action) {
  if(action.type=='GET_PRODUCT_BY_CATEGORY')
  {  
    let newState={...state};
    newState.data=action.data
    localStorage.setItem('ProductByCategory', JSON.stringify(newState));
    return newState;
  }
  else return state;
}
