import React, { Component } from 'react';
import { connect } from 'react-redux';
import { setOrder } from '../../redux/order_reducer';
import {Link} from 'react-router-dom';
class Detailoforder extends Component {
    constructor(props)
    {
        super(props);
        this.state = {};
    }
  
    render() {
    
     let {list_order_detail,currentUser,order}=this.props;
     console.log(order);
        return (
          <div>
            <div class="col-xs-6 col-sm-6 col-md-6 col-lg-6"> 
         <h1>Completing the order</h1>
         <form style={{marginLeft:"20px"}}>
         <label>UserName</label>
          <label className="form-control" >{currentUser.user_name} </label>  
          <label className="form-control" >{order.receiver} </label> 
           <br/>
         <label>Receiver</label>
         <input type="text" className="form-control" value={order.receiver} />
      <br/>
       <label>Receiving Address</label>
       <input type="text" className="form-control" value={order.receiving_address} />
      <br/>

     <label>Note</label>
     <input type="text" className="form-control" value={order.note} />
      <br/>
    <label>TotalPayment</label>
     <input type="text" value={order.total_payment}  className="form-control" readonly="true"  />
     <br/>
     <label>Status</label>
     {order.state=="0" && <div>
     <input type="text" value="processing"  className="form-control" />
     </div>}
     {order.state!="0" && <div>
     <input type="text" value="done"  className="form-control" />
     </div>
     }
     <button type="button" className="btn btn-lg btn-primary" style={{marginTop:"30px",backgroundColor:"#A52A2A"}} ><Link to="./order_history">Back</Link></button>
         </form>    
         </div>
         <div class="col-xs-6 col-sm-6 col-md-3 col-lg-6"> 
        
       
        }            
              
  
         </div>
         </div>

 
        )
    }
    
   
}
const mapStateToProps = state => {

    return {
        currentUser:state.loginState.currentUser,
        list_order_detail:state.orderDetailState.orderdetail,
        order:state.orderState.order
         }
}

export default connect(mapStateToProps,null)(Detailoforder);
