import axios from 'axios';
const GET_ALL_CATEGORY = 'GET_ALL_CATEGORY';
const GET_SUB_CATEGORY='GET_SUB_CATEGORY';
const GET_ALL_PRODUCT_BY_CATEGORY=' GET_ALL_PRODUCT_BY_CATEGORY';
export function getAllCategory() {
  return dispatch => {
     callCategoryApi(data => {
         dispatch(setAllCategory(data));//tra ve cho form
        
    });
  }
}
export function getSubCategory(id) {
   
  return dispatch => {
     callSubCategoryApi(id,data => {
         dispatch(setAllSubCategory(data));//tra ve cho form
        
    });
  }
}
export function getProductByCategory(id) {
  return dispatch => {
     callProductApi(id,data => {
         dispatch(setAllProduct(data));//tra ve cho form
        
    });
  }
}
function setAllCategory(allCategory) {
  console.log(allCategory);
  return {
    type: GET_ALL_CATEGORY,
    allCategory
  };
}
function setAllSubCategory(allSubCategory) {
  console.log(allCategory);
  return {
    type:GET_SUB_CATEGORY,
    allSubCategory
  };
}
function setAllProduct(allProduct)
{  return {
  type:GET_ALL_PRODUCT_BY_CATEGORY,
  allProduct
};

}
function callCategoryApi( callback) {
  axios.get(`http://127.0.0.1:3000/categories`)
  .then(res => {
  const  user = res.data;
    if (res.data!=null)
     {
      return callback(user);//not error
    } 
  }
  )

}
function callSubCategoryApi(id,callback)
{
  axios.get(`http://127.0.0.1:3000/categories?parent_id=`+id)
  .then(res => {
  const  sub_category = res.data;
    if (res.data!=null)
     {
      return callback(sub_category);//not error
    } 
  }
  )

}
function  callProductApi(id,callback)
{ 
  axios.get(`http://127.0.0.1:3000/products?category_id=`+id)
  .then(res => {
  const product = res.product;
    if (res.data!=null)
     {
      return callback(product);//not error
    } 
  }
  )
}
var category_state={
  allCategory:[],
  allSubCategory:[],
  allProduct:[],
}
export default function home_reducer(state =category_state, action) {
  if(action.type=='GET_ALL_CATEGORY')
  { let newState={...state};
    newState.allCategory=action.allCategory
    return newState;
  }
  if(action.type=='GET_SUB_CATEGORY')
  {
    let newState={...state};
    newState.allSubCategory=action.allSubCategory
    return newState;
  }
  if(action.type=='GET_ALL_PRODUCT_BY_CATEGORY')
  {
    let newState={...state};
    newState.allProduct=action.allProduct;
    return newState;
  }
  else return state;
}
