//Login
import axios from 'axios';
const GET_ALL_USER = 'GET_ALL_USER';
const UPDATE_USER='UPDATE_USER';
export function getAllUser() {
  console.log("abc"); 
  return dispatch => {
     callUserApi(data => {
         dispatch(setAllUser(data));//tra ve cho form
        
    });
  }
}
export function updateUser(id,user_name,email,address,password,newpassword) {
  console.log("abc"); 
  return dispatch => {
     callUpdateApi(id,user_name,email,address,password,newpassword,data => {
         dispatch(setUpdateUser(data));//tra ve cho form
        
    });
  }
}


function setAllUser(allUser) {
  console.log(allUser);
  return {
    type: GET_ALL_USER,
    allUser
  };
}
function setUpdateUser(user) {
  console.log(user);
  return {
    type:UPDATE_USER,
    user
  };
}
function callUserApi( callback) {
  axios.get(`http://127.0.0.1:3000/users`)
  .then(res => {
  const  users = res.data;
    if (res.data!=null)
     {
      return callback(users);//not error
    } 
  }
  )

}
function callUpdateApi(id,user_name,email,address,password,newpassword, callback) {
  console.log(password)
  axios.get(`http://127.0.0.1:3000/users?id=`+id)
  .then(res => {
  const  user = res.data;
    if (res.data!=null)
     { console.log(user);
       if(user.password==password)
        {  axios.put(`http://127.0.0.1:3000/users/`+id,{
           user_name:user_name,
           email:email,
           address:address,
           password:newpassword,

        }).then(res => {
         console.log(res.data);
         if(res.data!=null) 
         {  
          var currentUser={
            id:res.data.id,
            user_name:res.data.user_name,
           email:res.data.email,
           address:res.data.address,
         
           }
          
            localStorage.setItem('currentUser',JSON.stringify(currentUser))
             return callback(res.data);
         }
         else return callback(new Error("Invalid any field to update"));
        })

           
        }
        else callback(new Error("Old password is not correct to update your profile"))


      return callback(user);//not error
    } 
  }
  )

}
var data=JSON.parse(localStorage.getItem('currentUser'));
var dt=JSON.parse(localStorage.getItem('allUser'));
var users=dt? dt : [];
var user = data ? data : [];
var user_state={
  allUser:users,
  user:user,
}
export default function user_reducer(state =user_state, action) {
  if(action.type=='GET_ALL_USER')
  { let newState={...state};
    newState.allUser=action.allUser
    localStorage.setItem('allUser',JSON.stringify(newState.allUser))
    return newState;
  }
  if(action.type=='UPDATE_USER')
  { let newState={...state};
    newState.user=action.user
    return newState;
  }
  else return state;
}
