import React, { Component } from 'react';
import { connect } from 'react-redux';
class Complete extends Component {
    constructor(props)
    {
        super(props);
        
    }
    render() {
       
     var {currentUser,cart}=this.props;
        return (
          <div>
         <h1>Completing the order</h1>
         <form>
         <label>UserName</label>
          <label className="form-control">{currentUser.user_name} </label>  
           <br/>
         <label>Receiver</label>
         <input className="form-control" />
      <br/>
       <label>Receiving Address</label>
       <input className="form-control" />
      <br/>

     <label>Note</label>
     <input className="form-control" />
      <br/>
    <label>TotalPayment</label>
     <input value={this. showTotalAmount(cart)} className="form-control" readonly="true"/>
     <br/>
     <label>Status</label>
     <input value="processing" className="form-control" />
    
     <button type="button" className="btn btn-lg btn-primary"  style={{marginTop:"30px"}}>Order</button>
         </form>    
         </div>
 
        )
    }
    showTotalAmount = (cart) => {
        var total = 0;
        if (cart.length > 0) {
            for (var i = 0; i < cart.length; i++) {
                total += cart[i].product.price * cart[i].quantity;
            }
        }
        return total;
    }
}
const mapStateToProps = state => {
    return {
        cart: state.cartState,
        currentUser:state.loginState.currentUser
    }
}


export default connect(mapStateToProps, null)(Complete);
