import React from 'react';
import './User.css';
import {connect} from 'react-redux';
import {updateUser} from '../../redux/admin_reducer';
import {Link} from "react-router-dom";
import {  getAllUser,getUser} from '../../redux/user_reducer';
class User extends React.Component  {
  constructor(props)
  {
    super(props);
    this.state={};

  }
  componentDidMount()
    { 
     //this.props.getUser(this.props.currentUser.id);
    }
  render(){
    let {currentUser}=this.props;
    let {id,user_name,email,address,password,newpassword}=this.state;
      return (
     <div >
       <div className="UpdateImage">
        <div style={{paddingTop:"10px"}}>
          
        <div style={{color:"red",float:"left",marginRight:"5px",fontSize:"50px"}}>P</div>
        <div  style={{color:"yellow",float:"left",marginRight:"5px",fontSize:"50px"}}>R</div>  
        <div  style={{color:"greed",float:"left",marginRight:"5px",fontSize:"50px"}}>O</div>  
        <div  style={{color:"pink",float:"left",marginRight:"5px",fontSize:"50px"}}>F</div>  
        <div  style={{color:"blue",float:"left",marginRight:"5px",fontSize:"50px"}}>I</div> 
        <div  style={{color:"yellow",float:"left",marginRight:"5px",fontSize:"50px"}}>L</div> 
        <div  style={{color:"red",float:"left",marginRight:"5px",fontSize:"50px"}}>E</div>    
       
          <hr style={{width:"850px",marginLeft:"30px",marginTop:"10px",backgroundColor:"#000000"}}></hr>
        </div>
        <div className="container">
            <div className="col-xs-12 col-sm-12 col-md-12 col-lg-12">
              
              <div class="col-xs-2 col-sm-2 col-md-2 col-lg-2">
                
                <img  className="imageUpdate" src={require("./chonglee.jpg")} />
                <h1>{this.props.currentUser.user_name}</h1>
              </div>
              
              <div>
                  <div class="col-xs-4 col-sm-4 col-md-4 col-lg-4" style={{paddingLeft:"20px",marginTop:"20px"}}>
                      <div className="form-group">
                        <label >Username: </label>
                        <input type="text" className="form-control" style={{paddingRight:"10px"}} name="Username"  onChange={e => this.setState({user_name:e.target.value})} value={this.props.currentUser.user_name} placeholder="username" />
                      </div>
                      <div className="form-group">
                        <label >Email: </label>
                        <input type="text" className="form-control" name="Email" placeholder="email"  onChange={e => this.setState({email: e.target.value})} value={this.props.currentUser.email} />
                      </div>
                      <div className="form-group">
                        <label >Address: </label>
                        <input type="text" className="form-control" onChange={e => this.setState({address: e.target.value})}  name="Address" placeholder="address" value={this.props.currentUser.adddress} />
                      </div>
                      <div className="form-group">
                        <label >Old Password: </label>
                        <input type="password" className="form-control"  onChange={e => this.setState({password: e.target.value})}  name="Password"  />
                      </div>
                      <div className="form-group">
                        <label >New Password: </label>
                        <input type="password"  onChange={e => this.setState({newpassword: e.target.value})}  className="form-control" name="Password_Confimation"   />
                      </div>
                          
                      <button  style={{paddingLeft:"3px",marginLeft:"130px",backgroundColor:"#A52A2A"}} type="submit" className="btn btn-default" onClick={e=>{this.props.updateUser(id,user_name,email,address,password,newpassword);this.props.getAllUser()}}>Submit</button>
                  </div>
                
                  </div>
                
                 <div >
               <div>
                  
              </div>
            </div>
           </div>
          </div>
       </div>
     </div>
        
       
  );
}
}
const mapDispatchToProps = (dispatch) => {//store.dispatch(action)
  return {
  
    updateUser:(id,user_name,email,adddress,password,newpassword)=>dispatch(updateUser(id,user_name,email,adddress,password,newpassword)),
    getAllUser:()=>dispatch(getAllUser()),
  
    //action la login voi 2 tham so la email va password
  };
  }     
const mapStateToProps = state => {
return {
      currentUser:state.userState.user
  }
}
export default connect(mapStateToProps,mapDispatchToProps)(User);
