//Login
import axios from 'axios';
const GET_PRODUCT_FILTER = 'GET_PRODUCT_FILTER';
export function sort(by,value) {
    console.log("by");
    console.log(by);
    console.log("value");
    console.log(value);
  return dispatch => {
     callApi(by,value,data => {
         dispatch(set(data));//tra ve cho form
        
    });
  }
}
function set(data) {
  console.log(data);
  return {
    type: GET_PRODUCT_FILTER,
    data
  };
}
function callApi(by,value, callback) {

  axios.get(`http://127.0.0.1:3000/categories?id=1&id=2`)
  .then(res => {
  const  user = res.data;
    if (res.data!=null)
     {
      return callback(user);//not error
    } 
  }
  )

}


var data = JSON.parse(localStorage.getItem('Filter'));
var initialState = data ? data : [];
export default function filter_reducer(state =initialState, action) {
  if(action.type=='GET_PRODUCT_FILTER')
  {  
    let newState={...state};
    newState.data=action.data
    localStorage.setItem('Filter', JSON.stringify(newState));
    return newState;
  }

  else return state;
}
