import React, { Component } from 'react';
import { connect } from 'react-redux';
import { login } from '../../redux/reducer';
import './Login.css';

class Login extends React.Component  {
  constructor(props) {
    super(props);
    this.state = {};
    this.onSubmit = this.onSubmit.bind(this);
  }

  render(){
    let {email, password} = this.state;//state co 2 trang thai la email va password
    let {isLoginPending, isLoginSuccess, loginError} = this.props;
      return (
    
         <div>
           {console.log(this.props.login.email)}
       <div className="container mt-30" style={{marginLeft:"150px"}}>        
            <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">
                  <div  className="formlogin">
                    <div>
                    <label className="labellogin">LOGIN</label> 
                    <hr className="hrlogin" style={{borderTop: "1px solid black"}}></hr>
                    <img  src="https://d28avw9ny3vgf2.cloudfront.net/pbox/application/_img/salesite2018/common/pb-logo.png" className="imagelogin"/>
                    
                    </div>
                    <div className="form-group" style={{marginTop:"40px",marginLeft:"20px"}}>
                      <label >Email: </label>
                      <input type="text" className="form-control" style={{width:"500px"}} name="email" placeholder="Email" onChange={e => this.setState({email: e.target.value})} value={email} />
                    </div>
                    <div className="form-group" style={{marginLeft:"20px"}}>
                      <label >Password: </label>
                      <input type="password" style={{width:"500px"}} className="form-control" name="password" placeholder="Password" onChange={e => this.setState({password: e.target.value})} value={password}/>
                    </div>
                    
                    <div className="checkbox">
                      <label style={{marginLeft:"20px"}}>
                        <input type="checkbox" />
                        Remember me
                      </label>
                    </div>
                    
  
                    <button  style={{marginLeft:"220px"}}type="submit" className="btn btn-primary" onClick={this.onSubmit}>Login</button>
                    <h6 style={{marginLeft:"15px",marginTop:"10px",paddingBottom:"10px"}}>
                    Don't have an account yet? &nbsp;
                    <a href="#">Sign Up !</a>
                    </h6>
                   
                    
                  </div>
                  
                  </div>
              </div>
              <div className="message">
            { isLoginPending && <div>Please wait...</div> }
            { isLoginSuccess && <div>Success.</div> }
            { loginError && <div>{loginError.message}</div> }
            </div>
              
            </div>
          
        
       
          
       
  );
}
onSubmit(e) {
  e.preventDefault();
  let { email, password } = this.state;//lay trang thai off state do qua email va password
  this.props.login(email, password);//tao 1 props goi la login(email,password)
  this.setState({
    email: '',
    password: ''
  });
}
}

const mapStateToProps = (state) => {//tra state return ve tu reducer ve thanh prop
return {
  isLoginPending: state.isLoginPending,
  isLoginSuccess: state.isLoginSuccess,
  loginError: state.loginError
};
}

const mapDispatchToProps = (dispatch) => {//store.dispatch(action)
return {
  login: (email, password) => dispatch(login(email, password))//action la login voi 2 tham so la email va password
};
}

export default connect(mapStateToProps, mapDispatchToProps)(Login);
