import React from 'react';
import Dashboard from './Dashboard';
import { connect } from 'react-redux';
import {Link} from "react-router-dom";
import { getAllSubCategory} from '../../redux/home_reducer';
class AddProduct extends React.Component  {
    constructor(props)
    {
        super(props);
        this.state={};
    }
    change(event)
    {
      this.setState({value: event.target.value});
    }
  render(){
      let {category=[]}=this.props;
      let{value=""}=this.state;
      return (
     <div>     
    <Dashboard></Dashboard>
       <div >
           <div class="col-xs-9 col-sm-9 col-md-9 col-lg-9">
                <div >
                    <h3 style={{fontFamily:"'Times New Roman', Times, serif",color:"black"}} > Add Product</h3>
                    <hr style={{width:"1000px",marginLeft:"7px",marginTop:"5px",backgroundColor:"#000000"}}></hr>
                </div >
                <div>
                    <div class="col-xs-1 col-sm-1 col-md-1 col-lg-1" style={{marginLeft:"100px"}}>
                        
                        <img src="https://scontent.fdad3-3.fna.fbcdn.net/v/t1.15752-9/75456783_423624331920122_6258242227626049536_n.png?_nc_cat=108&_nc_oc=AQm1mi6o4BP2DTAiHAqHPdAW_x9F1tk-qoszc7YzGn8La-8qmMzzrGoWtmzNTVmTXXo&_nc_ht=scontent.fdad3-3.fna&oh=4a31fffe1c955697f1e2747dab448d3b&oe=5E4B5792" style={{width:"140%"}}/>
                        
                        <a href="" style={{fontFamily:"'Times New Roman', Times, serif",color:"black",paddingTop:"50px"}}>Add image</a>
                    </div>
                    <div class="col-xs-7 col-sm-7 col-md-7 col-lg-7" style={{marginLeft:"200px",marginTop:"13px"}}>
                        <div class="row">
                            
                            <div class="col-xs-4 col-sm-4 col-md-4 col-lg-4">
                            <select className="form-control"  onChange={event=>this.setState({value: event.target.value,by:"Category"})} >
            
                            {  this.props.category.length>0 &&
                                this.props.category.map(function(item, i){
                                return(<option>{item.category_name}</option>
                 
                                  )
                                 })
                             }
            
            } 
            </select>
                            </div>
                            <p style={{fontFamily:"'Times New Roman', Times, serif",fontSize:"20px"}}>List Category</p>
                        </div>
                    </div>
                    
                    <div class="col-xs-7 col-sm-7 col-md-7 col-lg-7" style={{marginTop:"80px"}}>
                        <div class="row">
                                <div class="col-xs-5 col-sm-5 col-md-5 col-lg-5">
                                    <input type="text"  class="form-control" placeholder="Product Detail"></input>    
                                </div>
                                <p style={{fontFamily:"'Times New Roman', Times, serif",fontSize:"21px"}}>Product Detail</p>
                        </div><br/>
                        <div class="row">
                                <div class="col-xs-5 col-sm-5 col-md-5 col-lg-5">
                                    <input type="text"  class="form-control" placeholder="Price" ></input>    
                                </div>
                                <p style={{fontFamily:"'Times New Roman', Times, serif",fontSize:"21px"}}>Price</p>
                        </div><br/>
                        <div class="row">
                                <div class="col-xs-5 col-sm-5 col-md-5 col-lg-5">
                                    <input type="text"  class="form-control" placeholder="Quantity"></input>    
                                </div>
                                <p style={{fontFamily:"'Times New Roman', Times, serif",fontSize:"21px"}}>Quantity</p>
                        </div><br/>
                    </div>
                </div>
               
                 </div>
             <button type="button" class="btn btn-primary" style={{marginLeft:"670PX",marginTop:"25px"}}>Add Product</button>
            
        </div>
        </div>       
           
  );
}
}
const mapStateToProps = (state) => {//tra state return ve tu reducer ve thanh prop
    return {
    //   isSignupPending: state.signupState.isSignupPending,
    //   isSignupSuccess: state.signupState.isSignupSuccess,
    //   signupError: state.signupState.signupError
   category:state.homeState. allSubCategory,
    };
    }
    
    const mapDispatchToProps = (dispatch) => {//store.dispatch(action)
    return {
    //   signup: (user_name,email,password,password_confirm,address) => dispatch(signup(user_name,email, password,password_confirm,address)),//action la login voi 2 tham so la email va password
    //   getAllUser:()=>dispatch(getAllUser())
    };
    }
    export default connect(mapStateToProps, mapDispatchToProps)(AddProduct);
