         {  
          var currentUser={
            id:res.data.id,
            user_name:res.data.user_name,
           email:res.data.email,
           address:res.data.address,
           is_admin:res.data.is_admin
         
           }
          
            localStorage.setItem('currentUser',JSON.stringify(currentUser))
             return callback(res.data);
         }
         else return callback(new Error("Invalid any field to update"));
        })

           
        }
        else callback(new Error("Old password is not correct to update your profile"))


      return callback(user);//not error
    } 
  }
  )

}



export function getUser(id) {
  console.log("id");
  console.log(id);
  return dispatch => {
     callApi(id,data => {
         dispatch(setUser(data));//tra ve cho form
        
    });
  }
}



function setUser(data) {
  console.log("user");
