var dt=JSON.parse(localStorage.getItem('allUser'));
var dt1=JSON.parse(localStorage.getItem('User'));
var users=dt? dt : [];
var user = data ? data : [];
var user_state={
  allUser:users,
  user:user,
  data:dt1?dt1:[],
  error:null,
}
export default function user_reducer(state =user_state, action) {
  if(action.type=='GET_ALL_USER')
  { let newState={...state};
    newState.allUser=action.allUser
    localStorage.setItem('allUser',JSON.stringify(newState.allUser))
    return newState;
  }
  if(action.type=='UPDATE_USER')
  { let newState={...state};
    newState.user=action.user
    if(newState.user!=null) alert("update sucessfully");
    return newState;
  }
  if(action.type=='GET_USER')
  {
    let newState={...state};
    newState.data=action.data;
    localStorage.setItem('User',JSON.stringify(newState.data))
    return newState;
  }
  if(action.type=='DELETE_USER')
  {
    let newState={...state};
    newState.error=action.error;

    return newState;
  }
  else return state;
}
