import React, { Component } from 'react';
import { connect } from 'react-redux';
import {Link} from 'react-router-dom';
import {  getAllProduct} from '../../redux/product_reducer';
class Order extends Component {
    constructor(props)
    {
        super(props);
        this.state = {};
    }
    componentWillMount()
    {
     this.props.getAllProduct();
    }
    render() {
    
     let {list_order_detail=[],order=[],product=[]}=this.props;
     let {id}=this.state;
    console.log("product of component");
    console.log(this.props.product);
    console.log(this.props.list_order_detail);
        return (
          <div>
            {this.props.order.length>0 &&
            <div>
            <div class="col-xs-6 col-sm-6 col-md-6 col-lg-6"> 
         <h1>Completing the order</h1>
         <form style={{marginLeft:"20px"}}>
         <label>UserName</label>
         
          <label className="form-control" > Suri Bui</label>  
           <br/>
         <label>Receiver</label>
         <input type="text" className="form-control" value={this.props.order[0].receiver} />
      <br/>
       <label>Receiving Address</label>
       <input type="text" className="form-control" value={this.props.order[0].receiving_address} />
      <br/>

     <label>Note</label>
     <input type="text" className="form-control" value={this.props.order[0].note} />
      <br/>
    <label>TotalPayment</label>
     <input type="text" value={this.props.order[0].total_payment}  className="form-control" readonly="true"  />
     <br/>
     <label>Status</label>
     {this.props.order[0].state=="0" && <div>
     <input type="text" value="processing"  className="form-control" />
     </div>}
     {this.props.order[0].state!="0" && <div>
     <input type="text" value="done"  className="form-control" />
     </div>
     }
     <button type="button" className="btn btn-lg btn-primary" style={{marginTop:"30px",backgroundColor:"#A52A2A"}} ><Link to="./orders">Back</Link></button>

         </form>    
         </div>
         <div class="col-xs-6 col-sm-6 col-md-3 col-lg-6"> 
         <table border="1">
                        <caption>List Order</caption>
                    <thead>
                        <tr>
                            <th>STT</th>
                            <th>Sản Phẩm</th>
                            <th>Giá</th>
                            <th>Số Lượng</th>
                            <th>Tổng Cộng</th>
                            
                        </tr>
                    </thead>
                   
           { this.props.list_order_detail.length>0&&
           this.props.list_order_detail.map((item, index) => {
              
                return (
                    <tr>
                      <td>{index}</td>
                    <td style={{paddingLeft:"10px"}}>
                        <h5>
                         { this.props.product.length>0 && <div>
                           {this.props.product.map((item1,i)=>
                           { if(item1.id==item.product_id)
                             return(                         
                            <h5><strong>{item1.product_name}</strong> </h5>
                             )
                             }
                           )
                            }  
                          </div>
                      }
                        </h5>
                    </td>
                    <td style={{paddingLeft:"10px"}}>{item.total_price/item.quantity}Đ</td>
                    <td style={{paddingLeft:"10px"}}>
                    <span className="qty">{item.quantity}</span>
                    </td>
                    <td>{item.total_price}Đ</td>
                    
                   </tr>
                 
                 
                );
            })
        }
        
       
       </table>
       
       <button type="button" className="btn btn-lg btn-primary" style={{marginTop:"400px",backgroundColor:"#A52A2A",marginLeft:"20px"}} ><Link to="./orders">Confirm</Link></button>    
              
  
         </div>
          </div>
            }
         </div>
      
 
        )
    }
    
   
}
const mapDispatchToProps = (dispatch) => {//store.dispatch(action)
  return {
   getAllProduct:()=>dispatch(getAllProduct())
};
  }     
const mapStateToProps = state => {
 
    return {
      
        //currentUser:state.loginState.currentUser,
        list_order_detail:state.orderDetailState.orderdetail,
        order:state.orderState.order,
        product:state.productState.allProduct,
         }
}

export default connect(mapStateToProps,mapDispatchToProps)(Order);
