import React, { Component } from 'react';
import './Slider.css';

class Slider extends React.Component
{   componentDidMount()
  {
  
      function doAnimations( elems ) {
        var animEndEv = 'webkitAnimationEnd animationend';
        elems.each(function () {
          animationType = this.data('animation');
          this.classList.add(animationType).addEventListener(animEndEv,function(){
            el.classList.remove(animationType);
            
          });
        });
      }
      //Variables on page load
      var myCarousel = this.getElementsByClassName('carousel-example-generic');
      var firstAnimatingElems =myCarousel.find('.item:first').find("[data-animation ^= 'animated']");
      //Initialize carousel
      myCarousel.carousel();
      //Animate captions in first slide on page load
      doAnimations(firstAnimatingElems);
      //Pause carousel
      myCarousel.carousel('pause');
      //Other slides to be animated on carousel slide event
      myCarousel.addEventListener('slide.bs.carousel', function (e) {
        var $animatingElems = (e.relatedTarget).find("[data-animation ^= 'animated']");
        doAnimations($animatingElems);
      });
    
     myCarousel.carousel({
        interval:300,
        pause: "false"
      });
  }
   render()
    {
      
    return(
    
     <div className="first-slider">
         <div id="carousel-example-generic" className="carousel slide carousel-fade">
           <ol className="carousel-indicators">
          <li data-target="#carousel-example-generic" data-slide-to="0"className="active"></li>
          <li data-target="#carousel-example-generic" data-slide-to="1"></li>
          <li data-target="#carousel-example-generic" data-slide-to="2"></li>
          <li data-target="#carousel-example-generic" data-slide-to="3"></li>
          <li data-target="#carousel-example-generic" data-slide-to="4"></li>
          <li data-target="#carousel-example-generic" data-slide-to="5"></li>
            </ol>
         
        <div className="carousel-inner" role="listbox">
      
          <div className="item active slide1">
          <img src={require('./images/slider1.jpg')} alt="Beautifull"/>
            <div className="row">
              <div className="container">
                <div className="col-md-9 text-left">
                  <h3 data-animation="animated bounceInDown"></h3>
                  <h4 data-animation="animated bounceInUp"></h4>
                </div>
              </div>
            </div>
          </div>
        
          <div className="item slide2">
            <div className="row">
              <div className="container">
                <div className="col-md-7 text-left">
                  <h3 data-animation="animated bounceInDown"></h3>
                  <h4 data-animation="animated bounceInUp"></h4>
                </div>
              </div>
            </div>
          </div>
         
          <div className="item slide3">
            <div className="row">
              <div className="container">
                <div className="col-md-7 text-left">
                  <h3 data-animation="animated bounceInDown"></h3>
                  <h4 data-animation="animated bounceInUp"></h4>
                </div>
              </div>
            </div>
          </div>
          <div className="item slide4">
            <div className="row">
              <div className="container">
                <div className="col-md-7 text-left">
                  <h3 data-animation="animated bounceInDown"></h3>
                  <h4 data-animation="animated bounceInUp"></h4>
                </div>
              </div>
            </div>
          </div>
          <div className="item slide5">
            <div className="row">
              <div className="container">
                <div className="col-md-7 text-left">
                  <h3 data-animation="animated bounceInDown"></h3>
                  <h4 data-animation="animated bounceInUp"></h4>
                </div>
              </div>
            </div>
          </div>
          <div className="item slide6">
            <div className="row">
              <div className="container">
                <div className="col-md-7 text-left">
                  <h3 data-animation="animated bounceInDown"></h3>
                  <h4 data-animation="animated bounceInUp"></h4>
                </div>
              </div>
            </div>
          </div>
          </div>
          <a className="left carousel-control" href="#carousel-example-generic"
          role="button" data-slide="prev">
          <i className="fa fa-angle-left"></i>
        </a>
        <a className="right carousel-control" href="#carousel-example-generic"
          role="button" data-slide="next">
          <i className="fa fa-angle-right"></i>
        </a>
      </div>
    </div>
    
    );
}
}
export default Slider;
