//Signup
import axios from 'axios';
const SET_SIGNUP_PENDING = 'SET_SIGNUP_PENDING';
const SET_SIGNUP_SUCCESS = 'SET_SIGNUP_SUCCESS';
const SET_SIGNUP_ERROR = 'SET_SIGNUP_ERROR';
export function signup(user_name,email,password,address) { //reducer thuc hien action login xu ly du lieu lay qua
  return dispatch => {
    dispatch(setSignupPending(true));
    dispatch(setSignupSuccess(false));
    dispatch(setSignupError(null));

    callSignupApi(user_name,email,password,address,error=> {
      dispatch(setSignupPending(false));//tra ve cho form
      if (!error) {
        dispatch(setSignupSuccess(true));//tra ve cho form
      } else {
        dispatch(setSignupError(error));//tra ve cho form
      }
    });
  }
}


function setSignupPending(isSignupPending) {
  return {
    type: SET_SIGNUP_PENDING,
    isSignupPending
  };
}

function setSignupSuccess(isSignupSuccess) {
  return {
    type: SET_SIGNUP_SUCCESS,
    isSignupSuccess
  };
}

function setSignupError(signupError) {
  return {
    type: SET_SIGNUP_ERROR,
    signupError
  }
}

function callSignupApi(user_name,email,password,address,callback) {
 const user={user_name:user_name,
             email:email,
             password:password,
             address:address};
 console.log(user);
  setTimeout(() => {
   axios.post(`http://127.0.0.1:3000/users`, { user })
      .then(res => {
        console.log(res);
        console.log(res.data);
        if(res.data!=null) 
        {
            return callback(null);
        }
        else return callback(new Error("Invalid any field to create a new user"))
      })
     
  }, 1000);

}
 
var signup_state={
  isSignupPending:false,
  signupError:null,
  isSignupSuccess:false,
}
export default function signup_reducer(state =signup_state, action) {
  if(action.type==' SET_SIGNUP_PENDING')
  {
      let newState={...state};
      newState.isSignupPending=action.isSignupPending;
      return newState;
  }
  if(action.type=='SET_SIGNUP_SUCCESS')
  {  let newState={...state};
    newState.isSignupSuccess=action.isSignupSuccess;
    return newState;

  }
  if(action.type=='SET_SIGNUP_ERROR')
  {  let newState={...state};
    newState.signupError=action.signupError;
    return newState;

  }
      return state;
  
}
