    render() {
    
     let {bill}=this.props;
        return (
         <div className="col-xs-6 col-sm-6 col-md-3 col-lg-6">
         <h1>Hóa đơn thanh toán</h1>
         <form style={{marginLeft:"20px"}}>
         <label>Tên cửa hàng</label>
          <label className="form-control">{bill.name}</label>  
           <br/>
