//Login
import axios from 'axios';
const GET_PRODUCT_BY_NAME = 'GET_PRODUCT_BY_NAME';
export function getProductByName(product_name) {
  return dispatch => {
     callGetProductByNameApi(product_name,data => {
        
        dispatch(setProductByName(data));
    });
  }
}



function setProductByName(allProduct) {
  console.log(allProduct);
  return {
    type: GET_PRODUCT_BY_NAME,
    allProduct
  };
}

function   callGetProductByNameApi(product_name, callback) {
  axios.get(`http://127.0.0.1:3000/products?product_name_like=^`+product_name)
  .then(res => {
  const  data = res.data;
    if (res.data!=null)
     { 
      return callback(data);//not error
    } 
  }
  )

}
var data = JSON.parse(localStorage.getItem('search'));
var initialState = data ? data : [];
export default function search_reducer(state =initialState, action) {
  if(action.type=='GET_PRODUCT_BY_NAME')
  { let newState={...state};
    newState.allProduct=action.allProduct
    localStorage.setItem('search', JSON.stringify(newState));
    return newState;
  }
  else return state;
}
