import React, { Component } from 'react';
import { connect } from 'react-redux';
import './Detail.css';
import { actAddToCart} from '../../redux/cart_reducer';
import { FacebookProvider, Comments} from 'react-facebook';
import {getColors,getSizes} from '../../redux/product_reducer';
import StarRatings from 'react-star-ratings';
import $ from 'jquery';
class Detail extends Component {
  
    constructor(props) {
        super(props);
        this.state = {rating:4};
        this.onAddToCart=this.onAddToCart.bind(this);
      }
   componentWillMount()
   {
     this.props.getSizes();
     this.props.getColors();
   }
   changeRating( newRating, name ) {
    this.setState({
      rating: newRating
    });
  }
render()
{  let {product,sizes=[],colors=[],rate=0} =this.props;
  let {isLogingSuccess,addProduct,quantity}=this.state;
    //this.state.rating=this.props.rate;   
  console.log("hdjsd");
  console.log(this.props);
  console.log(rate);
    return (
        <div style={{marginLeft:"100px"}}>
         
          {
           <div>
               
              <div className="row product-details">
        <nav className="navbar nav-menu details-brand">
         <div className="navbar-brand" style={{backgroundColor:"brown"}}>
       Chi tiết sản phẩm
    </div>
  </nav>
  <div className="card">
    <div className="container-fliud">
      <div className="wrapper row">
        <div className="preview col-md-6">
          <div className="preview-pic tab-content">
            <div className="tab-pane active" id="pic-1">
            <img className="img-responsive" onLoad={e => this.setState({addProduct:product})}  src={require('../../assets/'+product.image)} alt=""/>
            </div>
          </div>
          <ul className="preview-thumbnail nav nav-tabs">
            <li className="active">
              <a data-target="#pic-1" data-toggle="tab">
              <img className="img-responsive"  src={require('../../assets/'+product.image)} alt=""/>
              </a>
            </li>
            <li className="active">
              <a data-target="#pic-2" data-toggle="tab">
              <img className="img-responsive"  src={require('../../assets/'+product.image)} alt=""/>
              </a>
            </li>
            <li className="active">
              <a data-target="#pic-3" data-toggle="tab">
              <img className="img-responsive"  src={require('../../assets/'+product.image)} alt=""/>
              </a>
            </li>
            <li className="active">
              <a data-target="#pic-4" data-toggle="tab">
              <img className="img-responsive"  src={require('../../assets/'+product.image)} alt=""/>
              </a>
            </li>
            <li className="active">
              <a data-target="#pic-4" data-toggle="tab">
              <img className="img-responsive"  src={require('../../assets/'+product.image)} alt=""/>
              </a>
            </li>
          </ul>
        </div>
        <div className="details col-md-6">
          <h3 className="product-title">{product.product_name}</h3>
          <h3>Màu sắc</h3>
          <select style={{width:"100px"}}>
           {
             this.props.colors.length>0 &&
             this.props.colors.map((item,i)=>{
               var color=item.name;
               return(
                
             <option >{item.name}</option>
            
               )
             }
             )
           }
          </select>
          <h3>Kích thước</h3>
          <select style={{width:"100px"}}>
           {
             this.props.sizes.length>0 &&
             this.props.sizes.map((item,i)=>{
               //var color=item.name;
               return(
                
             <option >{item.name}</option>
            
               )
             }
             )
           }
          </select>
          <p className="product-description">
            {product.description}
          </p>
         <div className="pi-price">
                           Price:{product.price} Đ
                           <span> <button type="submit" className="shoe-cart pshoe-cart" onClick={this.onAddToCart}style={{width:"10%"}}><i className="fa fa-cart-plus" aria-hidden="true" /></button></span>               
                          </div>
         
          <br/>              
          {
                          <StarRatings
                          rating={this.state.rating}
                          starRatedColor="yellow"
                          changeRating={this.changeRating}
                          numberOfStars={5}
                          name='rating'
                        />
          }
                   
        

        </div>
      </div>
    </div>
  </div>
  </div>
  <div class="fb-comments" data-href="https://developers.facebook.com/docs/plugins/comments#configurator" data-width="" data-numposts="5"></div>
   </div>  
        }
     
        </div>

      )
  }
  onAddToCart(e)
  {  e.preventDefault();
    
   
    let { addProduct}  = this.state;
    
    //lay trang thai off state do qua email va password
    this.props.onAddToCart(addProduct);
   }
} 
const mapDispatchToProps = (dispatch) => {//store.dispatch(action)
  return {
       //onAddToCart:(addProduct) =>dispatch(actAddToCart(addProduct,1))
       getSizes:()=>dispatch(getSizes()),
       getColors:()=>dispatch(getColors())
};
  }      
const mapStateToProps = (state) => {//tra state return ve tu reducer ve thanh prop
    var a=false;
    if(localStorage.getItem('token')=="abcdefghiklm") a=true;
        return {
            product:state.productState.Product,
            isLogingSuccess:a,
            sizes:state.productState.Sizes,
            colors:state.productState.Colors,
            rate:state.productState.Rate,
        };
        }
    
export default connect(mapStateToProps,mapDispatchToProps)(Detail);
