//Login
import axios from 'axios';
const GET_ALL_PRODUCT='GET_ALL_PRODUCT'
const GET_PRODUCT_BY_ID='GET_PRODUCT_BY_ID'
export function getAllProduct() {
  return dispatch => {
     callProductApi(data => {
         dispatch(setAllProduct(data));//tra ve cho form
        
    });
  }
}
export function getProduct(id) {
  return dispatch => {
     callApi(id,data => {
         dispatch(setProduct(data));//tra ve cho form
        
    });
  }
}
function setAllProduct(allProduct) {
  return {
    type: GET_ALL_PRODUCT,
    allProduct
  };
}
function setProduct(product) {
  return {
    type: GET_PRODUCT_BY_ID,
    product
  };
}
function callProductApi( callback) {
  axios.get(`http://127.0.0.1:3000/products`)
  .then(res => {
  const  user = res.data;
    if (res.data!=null)
     {
      return callback(user);//not error
    } 
  }
  )

}
function callApi( id,callback) {
  axios.get(`http://127.0.0.1:3000/products?id=`+id)
  .then(res => {
  const  data = res.data;
    if (res.data!=null)
     {
      return callback(data);//not error
    } 
  }
  )

}
var product_state={

  allProduct:[],
  product:[],
}
export default function product_reducer(state =product_state, action) {
  
  if(action.type=='GET_ALL_PRODUCT')
  {  
  console.log(action.allProduct);
    let newState={...state};
    newState.allProduct=action.allProduct
    return newState;
  }
  if(action.type=='GET_PRODUCT_BY_ID')
  {  
    let newState={...state};
    newState.product=action.product
    return newState;
  }
  else return state;
}
