const SET_ORDER='SET_ORDER'
var data = JSON.parse(localStorage.getItem('ORDER'));
var initialState = data ? data : [];

 export function  setOrder(cartOrder,totalPayment) {
    return {
        type:SET_ORDER,
       cartOrder,
       totalPayment
    }
}

export default function order_reducer(state =initialState, action)  {
    var { cartOrder,totalPayment } = action;
   
    if(action.type=='SET_ORDER')
    {        
            
            localStorage.setItem('CART', JSON.stringify(state));
            return [...state];

        }       
   
     else return [...state];
    
}
