import React, { Component } from 'react';
import { Link ,Redirect} from "react-router-dom";
import { connect } from 'react-redux';
import { show} from '../../redux/product_detail_reducer';
import './Search.css';
class Search extends Component {
  constructor(props)
  {
    super(props);
    this.state = {};
    this.showDetail=this.showDetail.bind(this);
    this.addToCart=this.addToCart.bind(this);
  }
 

    render() {
        let { list_product_get_by_name,isLogingSuccess} = this.props;
        let { product_id}=this.state;
        console.log(this)
        console.log(this.props);
        return (
                <div>
                    <div className="category-featured digital">
                     <nav className="navbar nav-menu result-brand">
                      <div className="navbar-brand">
                      <span>
                      Kết Quả Tìm Kiếm
                    </span>
                
                   </div>
                </nav>
               <div className="product-featured clearfix">
               <div className="row"></div>
               <div className="col-sm-12 category-product"></div>
                 {   
                  this.props.list_product_get_by_name.map((item, i)=>{
                      let a =String(item.product_image);
                      return(
                       
                        <div className="col-sm-4">
                        <div className="product-item">
                          <div className="pi-img-wrapper">

                          <img className="img-responsive" onLoad={e => this.setState({product_id:item.id})}  src={require('../../assets/'+item.product_image)} alt=""/>
                            <div>
                             <button className="btn" onClick={this.showDetail}><Link to='./product_detail'>Detail of Product</Link></button>
                            </div>
                          </div>
                          <h3>
                          <Link to='./product_detail'>{item.product_name}</Link>
                          </h3>
                          <div className="pi-price">
                           Price:{item.price}
                          </div>
                           <div className="pi-quantity"> 
                           <input type="number" name="quantity" min="1" max={item.quantity} />
                            </div> 
                            <div className="sticker sticker-new">
                          </div>
                            <div>
                             <button className="btn add2cart" onCLick={this.addToCart}> Add into cart</button>
                            
                            </div>
                        </div>
                      </div>


                      )
                 })
                }
                 </div>
               </div>
   
            </div>
          

                
            );
}   
showDetail(e)
{
  e.preventDefault();
  let { product_id } = this.state;//lay trang thai off state do qua email va password
  this.props.show(product_id);
 
}
addToCart(e)
{
  e.preventDefault();
  if(!this.props.isLogingSuccess)  return <Redirect to='./login'></Redirect>
}

}
const mapDispatchToProps = (dispatch) => {//store.dispatch(action)
return {
  show:(product_id) => dispatch(show(product_id))//action la login voi 2 tham so la email va password
};
}     
const mapStateToProps = (state) => {//tra state return ve tu reducer ve thanh prop
  var a=false;
    if(localStorage.getItem('token')=="abcdefghiklm") a=true;
    return {
      list_product_get_by_name:state.searchState.allProduct,
      isLogingSuccess:a
    };
    }

  export default connect(mapStateToProps,mapDispatchToProps)(Search);
