import React from 'react';
import Dashboard from './Dashboard';
import { connect } from 'react-redux';
import {Link} from "react-router-dom";
import {  getAllOrder} from '../../redux/order_reducer';
import {  getOrderDetail} from '../../redux/order_detail_reducer';
import {getOrderById,deleteOrder} from '../../redux/order_reducer';
import {getAllUser} from '../../redux/user_reducer';
class ManagerOrder extends React.Component  {
    constructor(props)
    {
        super(props);
        this.state={};
    }
    componentWillMount()
    {
        this.props.getAllOrder();
        this.props.getAllUser();
    }

  render(){
      let {orders=[],error=null,users=[]}=this.props;
      let {id}=this.state;
      if(error!=null) alert("delete successfully!!")
      return (
          
         <div>
          <Dashboard></Dashboard>
          <div>
              
              <div class="col-xs-9 col-sm-9 col-md-9 col-lg-9">
                 <div >
                   
                    <div >
                        <h2 style={{fontFamily:"'Times New Roman', Times, serif"}}>Manager Orders</h2>
                        <hr style={{width:"300px",marginLeft:"7px",marginTop:"5px",backgroundColor:"#000000"}}></hr>
                    </div >
                       
                    <table class="table table-striped" style={{fontSize:"20px",fontFamily:"Times New Roman', Times, serif"}}>
                    <thead>
                        <tr>
                            <th>No</th>
                            <th>ID</th>
                            <th>Date</th>
                            <th>UserName</th>
                            <th>Totalpayment</th>
                            <th>State</th>
                             <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                     {this.props.orders.length>0 &&
                       this.props.orders.map((item,i)=>{
                           return(
                            
                            <tr>  
                            <td>{i}</td>
                            <td>{item.id}</td>
                            {/* <td>{item.user_name}</td> */}
                            <td>22/11/2019</td>
                            {this.props.users.length>0 &&
                            this.props.users.map((item1,i)=>
                            { if(item.user_id==item1.id)   
                              return(
                            <td>{item1.user_name}</td>
                              )
                              })
                            }  
                            <td>{item.total_payment}</td>
                            { item.state=="0" && 
                             <td>processing</td>
                               

                            }
                              { item.state=="1" && 
                             <td>Done</td>
                               }
                            <td> <button className="btn" style={{backgroundColor:"#A52A2A"}}
                            onClick={e =>{

                            this.props.getOrderDetail(item.id);
                            this.props.getOrderById(item.id);
                            }}><Link to='./orderdetail'>Detail</Link> </button>
                                       
                                            
                                         
                                |
                                <button className="btn" style={{backgroundColor:"#A52A2A"}}onClick={e =>{
                                        // //  this.props.getOrderDetail(item.id);
                                        // //  this.props.getOrderById(item.id);
                                        this.props.deleteOrder(item.id);
                                        this.props.getAllOrder();
                                         }
                                         }><Link to='./orders'>Delete</Link></button></td>
                                

                            </tr>
                       

                           )
                       }
                       )
                     }

                    </tbody>
                    </table>
                </div>
            </div>
              
            
      </div>
      </div>
  );
}
}
const mapStateToProps = (state) => {//tra state return ve tu reducer ve thanh prop
    
    return {
        orders:state.orderState.allOrder,
        users:state.userState.allUser,
        
    };
  }
const mapDispatchToProps = (dispatch) => {//store.dispatch(action)
      return {
          getAllOrder:()=>dispatch(getAllOrder()),
           getOrderDetail:(id)=>dispatch(getOrderDetail(id)),
        getOrderById:(id)=>dispatch(getOrderById(id)),
        deleteOrder:(id)=>dispatch(deleteOrder(id)),
        getAllUser:()=>dispatch(getAllUser())
    };
      }     
export default connect(mapStateToProps,mapDispatchToProps)(ManagerOrder);
