import React, { Component } from 'react';
import { connect } from 'react-redux';
import { setOrder } from '../../redux/order_reducer';
class Complete extends Component {
    constructor(props)
    {
        super(props);
        this.Order=this.Order.bind(this);
    }
    render() {
    var {order,user_id,state,receiving_address,receiver,total_payment,note}=this.state;
     var {currentUser,cart}=this.props;
        return (
          <div>
         <h1>Completing the order</h1>
         <form>
         <label>UserName</label>
          <label className="form-control" onChange={e => this.setState({user_id:currentUser.id})} >{currentUser.user_name} </label>  
           <br/>
         <label>Receiver</label>
         <input className="form-control" onChange={e => this.setState({receiver:e.target.value})}/>
      <br/>
       <label>Receiving Address</label>
       <input className="form-control" onChange={e => this.setState({receiving_address:e.target.value})} />
      <br/>

     <label>Note</label>
     <input className="form-control" onChange={e => this.setState({note:e.target.value})}/>
      <br/>
    <label>TotalPayment</label>
     <input value={this. showTotalAmount(cart)} className="form-control" readonly="true" onChange={e => this.setState({total_payment:e.target.value,order:cart[0]})} />
     <br/>
     <label>Status</label>
     <input value="processing" className="form-control" onChange={e => this.setState({state:0})} />
    
     <button type="button" className="btn btn-lg btn-primary"  style={{marginTop:"30px",backgroundColor:"#A52A2A"}} >Order</button>
         </form>    
         </div>
 
        )
    }
    showTotalAmount = (cart) => {
        var total = 0;
        if (cart.length > 0) {
            for (var i = 0; i < cart.length; i++) {
                total += cart[i].product.price * cart[i].quantity;
            }
        }
        return total;
    }
    Order(e)
    {  e.preventDefault();
        let { order,user_id,state,receiving_address,receiver,total_payment,note } = this.state;
        console.log(order);//lay trang thai off state do qua email va password
        this.props.setOrder(order,user_id,state,receiving_address,receiver,total_payment,note);//tao 1 props goi la login(email,password)
   
     
    }
}
const mapStateToProps = state => {

    return {

        cart: state.cartState,
        currentUser:state.loginState.currentUser
    }
}
const mapDispatchToProps = (dispatch) => {//store.dispatch(action)
    return {
      setOrder: (order,user_id,state,receiving_address,receiver,total_payment,note) => dispatch(setOrder(order,user_id,state,receiving_address,receiver,total_payment,note))//action la login voi 2 tham so la email va password
    };
    }

export default connect(mapStateToProps, null)(Complete);
