import axios from 'axios';

const GET_ORDER_DETAIL='GET_ORDER_DETAIL';   
const GET_PRODUCT='GET_PRODUCT';
export function getOrderDetail(id) {
    return dispatch => {
       callDetailApi(id,data => {
        dispatch(setDetail(data));
        //tra ve cho form
          
      });
    }
  }
  export function getProduct(id) {
    return dispatch => {
       callApi(id,data => {
        dispatch(set(data));
        //tra ve cho form
          
      });
    }
  }

function setDetail(orderdetail) {
    console.log(orderdetail);
    return {
      type: GET_ORDER_DETAIL,
     orderdetail
    };
  }
  function set(product) {
    console.log(product);
    return {
      type: GET_PRODUCT,
      product
    };
  }

function  callDetailApi(id,callback) {
  axios.get(`http://127.0.0.1:3000/order_detail?order_id=`+id)
  .then(res => {
  const  orders = res.data;
    if (res.data!=null)
     {
        return callback(orders)
    }
      
  }
  )

}
function  callApi(id,callback) {
  axios.get(`http://127.0.0.1:3000/order_detail?order_id=`+id)
  .then(res => {
  const  orders = res.data;
    if (res.data!=null)
     { console.log(orders);
       var list_product=[];
       console.log(orders.length);
       if (orders.length > 0) {
        for (var i = 0; i < orders.length; i++) {
          axios.get(`http://127.0.0.1:3000/products?id=`+orders[i].product_id)
          .then(res => {
             list_product.push(res.data);
          })
        }
        return callback(list_product)
    }
     } 
  }
  )

}
var data = JSON.parse(localStorage.getItem('OrderDetail'));
var initialState =data ? data : [];
 
export default function order_detail_reducer(state =initialState, action) {
  if(action.type=='GET_ORDER_DETAIL')
  {
      let newState={...state};
      newState.orderdetail=action.orderdetail
      localStorage.setItem('OrderDetail', JSON.stringify(newState));
      return newState;
      
  }
  if(action.type=='GET_PRODUCT')
  { console.log("product");
    console.log(action.product);
    let newState={...state};
    newState.product=action.product
    return newState;
  }
  else return state;
}
