import React, { Component } from "react";
import { getAllUser} from '../../redux/user_reducer';
import { connect } from 'react-redux';
import Pagination from './Panigation';
class Test extends Component {
  state = {
    allUsers: [],
    currentUsers: [],
    currentPage: null,
    totalPages: null
  };

  componentDidMount() {
    this.props.getAllUser();
    const allUsers=this.props.allUsers;
    console.log(allUsers);
    this.setState({ allUsers });
  }

  onPageChanged = data => {
    const { allUsers } = this.state;
    const { currentPage, totalPages, pageLimit } = data;

    const offset = (currentPage - 1) * pageLimit;
    const currentUsers = allUsers.slice(offset, offset + pageLimit);

    this.setState({ currentPage, currentUsers, totalPages });
  };

  render() {
    const {
      allUsers,
      currentUsers,
      currentPage,
      totalPages
    } = this.props;
    const totalUsers = allUsers.length;

    if (totalUsers === 0) return null;

    const headerClass = [
      "text-dark py-2 pr-4 m-0",
      currentPage ? "border-gray border-right" : ""
    ]
      .join(" ")
      .trim();

    return (
      <div className="container mb-5">
        <div className="row d-flex flex-row py-5">
          <div className="w-100 px-4 py-5 d-flex flex-row flex-wrap align-items-center justify-content-between">
            <div className="d-flex flex-row align-items-center">
              <h2 className={headerClass}>
                <strong className="text-secondary">{totalUsers}</strong>{" "}
                Users
              </h2>
              {currentPage && (
                <span className="current-page d-inline-block h-100 pl-4 text-secondary">
                  Page <span className="font-weight-bold">{currentPage}</span> /{" "}
                  <span className="font-weight-bold">{totalPages}</span>
                </span>
              )}
            </div>
            <div className="d-flex flex-row py-4 align-items-center">
              <Pagination
                totalRecords={totalUsers}
                pageLimit={1}
                pageNeighbours={1}
                onPageChanged={this.onPageChanged}
              />
            </div>
          </div>
          {
          this.props.allUsers.map((item, i)=>{
                   
                      return(
                         <div>{item.user_name}</div>
                      )})
          }           
        </div>
      </div>
    );
  }
}
const mapDispatchToProps = (dispatch) => {//store.dispatch(action)
    return {
         getAllUser:() =>dispatch(getAllUser())
  };
}      
  const mapStateToProps = (state) => {//tra state return ve tu reducer ve thanh prop
        
          return {
              allUsers:state.userState.allUser,
            
          };
          }
      
  export default connect(mapStateToProps,mapDispatchToProps)(Test);
