import axios from 'axios';
const GET_PRODUCT_DETAIL = 'GET_PRODUCT_DETAIL';    
export function show(id) {

  return dispatch => {
     callProductApi(id,data => {
        
        dispatch(setProduct(data));//tra ve cho form
        
    });
  }
}



function setProduct(Product) {
  return {
    type: GET_PRODUCT_DETAIL,
    Product
  };
}

function  callProductApi( id,callback) {
  axios.get(`http://127.0.0.1:3000/products?id=`+id)
  .then(res => {
  const  product = res.data;
    if (res.data!=null)
     { localStorage.setItem('productDetail',JSON.stringify(product));
      return callback(product);//not error
    } 
  }
  )

}
var product_state={
    Product:[],

}
export default function product_detail_reducer(state =product_state, action) {
  if(action.type=='GET_PRODUCT_DETAIL')
  { let newState={...state};
    newState.Product=action.Product
    return newState;
  }
  else return state;
}
