import React from 'react';
import './Menu.css';
import {getProductByCategory,getCategoryName} from '../../redux/menu_reducer';
import {sort} from '../../redux/filter_reducer';
import {Link} from 'react-router-dom';
import { connect } from 'react-redux';
class Menu extends React.Component{
    constructor(props)
    {
        super(props);
        this.state = {value:"",by:""};
        this.change=this.change.bind(this);
    }
    componentDidMount()
    {//this.props.getAllSubCategory();
     //this.props.getAllCategory();
    }
    change(event)
    {
      this.setState({value: event.target.value});
    }
    changeb(event)
    {
      this.setState({by:"dcm"});
    }
    render()
    {  let {list_cate,list_sub}=this.props;
      let {id}=this.state;
    console.log(this.props);
        return(
            <div>
             <div className="nav-side-menu">
              <div className="brand">
                <h1>Menu</h1>
             </div>
            <i className="fa fa-bars fa-2x toggle-btn" data-toggle="collapse" data-target="#menu-content"></i>
             <div className="menu-list">
             <ul id="menu-content" className="menu-content collapse out">
                  {/*check list category cha**/}
                  {
                  this.props.list_cate.map((item, i)=>{
                      return(
                   <div>
                  <li data-toggle="collapse" data-target={item.category_name} className="collapsed active">
                    <Link to="./category" onClick={e=>{this.props.getProductByCategoryId(item.id);this.props.getCategoryName(item.id)}}>
                  <i className="fa fa-dashboard fa-lg"></i>
                  {/*Link to product of category cha*/}
                   {item.category_name}
                  <span className="arrow"></span>
                  </Link>
                 </li>
                 {/* <ul className="sub-menu collapse" id={item.category_name}> */}
                 { 
                    this.props.list_sub.map((item1,i)=>
                    { if(item1.parent_id==item.id)
                      return(
                        
                         <li>
                            <Link to="./category" onClick={e=>{this.props.getProductByCategoryId(item1.id);this.props.getCategoryName(item1.id)}}>
                            {item1.category_name}
                            </Link>
                           </li>
                       
                    )
                    })
                      
                    
                 }
               
            
             </div>
              
                 )})
                }
              
               </ul>
          
             </div>

  </div>
<div className="filter">
 
    <div className="input-group">
      <span className="input-group-addon">
      Category
      </span>
      <span>
      { <select className="form-control"  onChange={event=>this.setState({value: event.target.value,by:"Category"})} >
            
            { this.props.list_sub.map(function(item, i){
                 return(<option >{item.category_name}</option>
                 
                 )
                })
                 }
            
            } 
            </select>
      }
      </span>
    </div>
    <div className="input-group">
      <span className="input-group-addon">
       
      Price
      </span>
      <select className="form-control" onChange={event=>this.setState({value: event.target.value,by:"Price"})} >
        <option selected>1-300000</option>
       <option >300000-500000</option>
        <option>500000-1000000</option>
        </select>
      <span>
       
      </span>
    </div>
    <div className="input-group">
      <span className="input-group-addon" >
      Rating
      </span>
      <span>
      <select className="form-control"  onChange={event=>this.setState({value: event.target.value,by:"Rating"})}>
        <option selected>0-3</option>
       <option >4-6</option>
        <option>7-10</option>
        </select>
      </span>
    </div>
    <div className="input-group">
      <span className="input-group-addon">
       Sort
      </span>
      <select className="form-control" onChange={event=>this.setState({value: event.target.value,by:"Sort"})}>
       <option selected >Alphabet</option>
        <option >Price</option>
        </select>
      <span>
     
      </span>
    </div>
 
    
     <button style={{marginLeft:"50px",marginTop:"20px",backgroundColor:"#A52A2A"}} type="submit" className="btn btn-primary" onClick={e=>{this.props.sort(this.state.by,this.state.value)}}><Link to="./filter">Filter</Link></button>
     
      
   </div>
  </div>
        )
    }
}

const mapDispatchToProps = (dispatch) => {//store.dispatch(action)
  return {
    sort:(by,value)=>dispatch(sort(by,value)),
    getProductByCategoryId:(id)=>dispatch(getProductByCategory(id)),
    getCategoryName:(id)=>dispatch(getCategoryName(id))
  };
  }     
  const mapStateToProps = (state) => {//tra state return ve tu reducer ve thanh prop
   
      return {
        list_cate:state.homeState.allCategory,
        list_sub:state.homeState.allSubCategory,
    };
      }
  
    export default connect(mapStateToProps,mapDispatchToProps)(Menu);
