import axios from 'axios';
const GET_ORDER_HISTORY= 'GET_ORDER_HISTORY';    
export function getOrderHistory() {
  return dispatch => {
     callApi(data => {
      dispatch(set(data));//tra ve cho form
        
    });
  }
}



function set(orders) {
  console.log(orders);
  return {
    type: GET_ORDER_HISTORY,
    orders
  };
}

function  callApi(callback) {
    let user=JSON.parse(localStorage.getItem('currentUser'));
  axios.get(`http://127.0.0.1:3000/orders?user_id=`+user.id)
  .then(res => {
  const  orders = res.data;
    if (res.data!=null)
     { 
          return callback(orders);//not error
     } 
  }
  )

}
var data = JSON.parse(localStorage.getItem('OrderHistory'));
var initialState = data ? data : [];
export default function order_history_reducer(state =initialState, action) {
  if(action.type=='GET_ORDER_HISTORY')
  { let newState={...state};
    newState.orders=action.orders
    localStorage.setItem('OrderHistory', JSON.stringify(newState));
    return newState;
  }
  else return state;
}
