import React, { Component } from 'react';
import { connect } from 'react-redux';
import { login } from '../../redux/reducer';
import './Login.css';

class Login extends React.Component  {
  render(){
      return (
      
         <div>
           {console.log(this.props.login)}
       <div className="container mt-30" style={{marginLeft:"150px"}}>        
            <div className="col-xs-6 col-sm-6 col-md-6 col-lg-6">
                  <div  className="formlogin">
                    <div>
                    <label className="labellogin">LOGIN</label> 
                    <hr className="hrlogin" style={{borderTop: "1px solid black"}}></hr>
                    <img  src="https://d28avw9ny3vgf2.cloudfront.net/pbox/application/_img/salesite2018/common/pb-logo.png" className="imagelogin"/>
                    
                    </div>
                    <div className="form-group" style={{marginTop:"40px",marginLeft:"20px"}}>
                      <label >Username: </label>
                      <input type="text" className="form-control" style={{width:"500px"}} name="Username" placeholder="Username"  />
                    </div>
                    <div className="form-group" style={{marginLeft:"20px"}}>
                      <label >Password: </label>
                      <input type="password" style={{width:"500px"}} className="form-control" name="Password" placeholder="Password" />
                    </div>
                    
                    <div className="checkbox">
                      <label style={{marginLeft:"20px"}}>
                        <input type="checkbox" />
                        Remember me
                      </label>
                    </div>
                    
  
                    <button  style={{marginLeft:"220px"}}type="submit" className="btn btn-primary" >Login</button>
                    <h6 style={{marginLeft:"15px",marginTop:"10px",paddingBottom:"10px"}}>
                    Don't have an account yet? &nbsp;
                    <a href="#">Sign Up !</a>
                    </h6>
                   
                    
                  </div>
                  
                  </div>
              </div>
              
              
            </div>
          
        
       
          
       
  );
}
}
const mapStateToProps = (state) => {//tra state return ve tu reducer ve thanh prop
  return {
    login:state.login
  };
}

export default connect(mapStateToProps,null)(Login);
