import axios from 'axios';
const GET_HOT_PRODUCTS = 'GET_HOT_PRODUCTS';
export function getHotProduct() {
  console.log("hot product"); 
  return dispatch => {
     callProductApi(data => {
        
        dispatch(setHotProduct(data));//tra ve cho form
        
    });
  }
}



function setHotProduct(hotProduct) {
  console.log(hotProduct);
  return {
    type: GET_HOT_PRODUCT,
    hotProduct
  };
}

function  callProductApi( callback) {
  axios.get(`http://127.0.0.1:3000/products?hot_product=true`)
  .then(res => {
  const  hot_product = res.data;
    if (res.data!=null)
     {
      return callback(hot_product);//not error
    } 
  }
  )

}
var product_state={
    hotProduct:[],

}
export default function hot_product_reducer(state =product_state, action) {
  if(action.type=='GET_HOT_PRODUCTS')
  { let newState={...state};
    newState.hotProduct=action.hotProduct
    return newState;
  }
  else return state;
}
