import React from 'react';
import './Menu.css';
import {  getAllCategory,getAllSubCategory} from '../../redux/home_reducer';
import { connect } from 'react-redux';
class Menu extends React.Component{
    constructor(props)
    {
        super(props);
        this.state = {};
    }
    componentDidMount()
    {//this.props.getAllSubCategory();
     //this.props.getAllCategory();
    }
    render()
    {  let {list_cate,list_sub}=this.props;
    console.log(this.props);
        return(
            <div>
             <div className="nav-side-menu">
              <div className="brand">
                <h1>Menu</h1>
             </div>
            <i className="fa fa-bars fa-2x toggle-btn" data-toggle="collapse" data-target="#menu-content"></i>
             <div className="menu-list">
             <ul id="menu-content" className="menu-content collapse out">
                  {/*check list category cha**/}
                  {
                  this.props.list_cate.map((item, i)=>{
                      return(
                  <div>     
                  <li data-toggle="collapse" data-target={item.category_name} className="collapsed active">
                  <i className="fa fa-dashboard fa-lg"></i>
                  {/*Link to product of category cha*/}
                   {item.category_name}
                  <span className="arrow"></span>
                 </li>
                 <ul class="sub-menu collapse" id="<%= category.name %>">
                 { 
                    this.props.list_sub.map((item1,i)=>
                    { if(item1.parent_id==item.id)
                      return(
                     
                     
                          <li>
                          <Link to="./ " ></Link>
                          {item1.category_name}
                          </li>
                     
                      
                    )
                    })
                      
                    
                 }
                </ul>
            
          
                 </div>
                 )})
                }
              
               </ul>
          
             </div>

  </div>
<div className="filter">
 
    <div className="input-group">
      <span className="input-group-addon">
      Category
      </span>
      <span>
      { <select className="form-control">
            
            { this.props.list_sub.map(function(item, i){
                 return(
                  
                  <option selected>{item.category_name}</option>
                 
                 )
                })
                 }
            
            } 
            </select>
      }
      </span>
    </div>
    <div className="input-group">
      <span className="input-group-addon">
      Price
      </span>
      <select className="form-control">
        <option selected>1-300000</option>
       <option value="1">300000-500000</option>
        <option value="2">500000-1000000</option>
        <option value="3">>1000000</option>
        </select>
      <span>
       
      </span>
    </div>
    <div className="input-group">
      <span className="input-group-addon">
      Rating
      </span>
      <span>
      <select className="form-control">
        <option selected>0-3</option>
       <option value="1">4-6</option>
        <option value="2">7-10</option>
        </select>
      </span>
    </div>
    <div className="input-group">
      <span className="input-group-addon">
       Sort
      </span>
      <select className="form-control">
        <option selected>None</option>
       <option value="1">Alphabet</option>
        <option value="2">Price</option>
        </select>
      <span>
     
      </span>
    </div>
    <div className="input-group">
      <span className="input-group-addon">
      By
      </span>
      <span>
      <select className="form-control">
        <option selected>Ascending</option>
       <option value="1">Descending</option>
        <option value="2">Price</option>
        </select>
      </span>
    </div>
      
    
     <button style={{marginLeft:"50px",marginTop:"20px"}} type="submit" className="btn btn-primary">Filter</button>
     
      
   </div>
  </div>
        )
    }
}

const mapDispatchToProps = (dispatch) => {//store.dispatch(action)
  return {
     //getAllCategory: () => dispatch(getAllCategory()),
    //getAllSubCategory:()=>dispatch(getAllSubCategory())
    //action la login voi 2 tham so la email va password
  };
  }     
  const mapStateToProps = (state) => {//tra state return ve tu reducer ve thanh prop
   
      return {
        list_cate:state.homeState.allCategory,
        list_sub:state.homeState.allSubCategory,
    };
      }
  
    export default connect(mapStateToProps,mapDispatchToProps)(Menu);
