import React, { Component } from 'react';
import { connect } from 'react-redux';
import { getProduct} from '../../redux/product_reducer';
import {Link} from 'react-router-dom';
class Detailoforder extends Component {
    constructor(props)
    {
        super(props);
        this.state = {};
    }
  
    render() {
    
     let {list_order_detail,currentUser,order,product}=this.props;
     let {id}=this.state;
     console.log(this.props.product);
        return (
        
          <div>
            <div class="col-xs-6 col-sm-6 col-md-6 col-lg-6"> 
         <h1>Completing the order</h1>
         <form style={{marginLeft:"20px"}}>
         <label>UserName</label>
          <label className="form-control" >{currentUser.user_name} </label>  
           <br/>
         <label>Receiver</label>
         <input type="text" className="form-control" value={order[0].receiver} />
      <br/>
       <label>Receiving Address</label>
       <input type="text" className="form-control" value={order[0].receiving_address} />
      <br/>

     <label>Note</label>
     <input type="text" className="form-control" value={order[0].note} />
      <br/>
    <label>TotalPayment</label>
     <input type="text" value={order[0].total_payment}  className="form-control" readonly="true"  />
     <br/>
     <label>Status</label>
     {order[0].state=="0" && <div>
     <input type="text" value="processing"  className="form-control" />
     </div>}
     {order[0].state!="0" && <div>
     <input type="text" value="done"  className="form-control" />
     </div>
     }
     <button type="button" className="btn btn-lg btn-primary" style={{marginTop:"30px",backgroundColor:"#A52A2A"}} ><Link to="./order_history">Back</Link></button>
         </form>    
         </div>
         <div class="col-xs-6 col-sm-6 col-md-3 col-lg-6"> 
         <table border="1">
                        <caption>List Order</caption>
                    <thead>
                        <tr>
                            <th>STT</th>
                            <th>Sản Phẩm</th>
                            <th>Giá</th>
                            <th>Số Lượng</th>
                            <th>Tổng Cộng</th>
                            
                        </tr>
                    </thead>
                   
           {
           this.props.list_order_detail.map((item, index) => {
                this.props.getProduct(item.product_id);
                return (
                    <tr>
                      <td>{index}</td>
                    <td style={{paddingLeft:"10px"}}>
                        <h5>
                            <strong>{this.props.product.product_name}</strong> 
                        </h5>
                    </td>
                    <td style={{paddingLeft:"10px"}}>{item.total_price/item.quantity}Đ</td>
                    <td style={{paddingLeft:"10px"}}>
                    <span className="qty">{item.quantity}</span>
                    </td>
                    <td>{item.total_price}Đ</td>
                    
                   </tr>
                 
                 
                );
            })
        }
        
       
       </table>
       
            
              
  
         </div>
         </div>

 
        )
    }
    
   
}
const mapDispatchToProps = (dispatch) => {//store.dispatch(action)
  return {
  getProduct:(id)=>dispatch(getProduct(id))
   
};
  }     
const mapStateToProps = state => {
    
    return {
       product:state.productState.product,
        currentUser:state.loginState.currentUser,
        list_order_detail:state.orderDetailState.orderdetail,
       order:state.orderState.order,
         }
}

export default connect(mapStateToProps,mapDispatchToProps)(Detailoforder);
