import React, { Component } from 'react';
import { Link ,Redirect} from "react-router-dom";
import { connect } from 'react-redux';
class Search extends Component {
  constructor(props)
  {
    super(props);
    this.state = {};
  }
    render() {
        let { list_product_get_by_name} = this.props;
        console.log(this.props);
        return (
                <div>
                 {
                  this.props.list_product_get_by_name.map(function(item, i){
                      return(
                  <li data-toggle="collapse" data-target={item} className="collapsed active">
                  <i className="fa fa-dashboard fa-lg"></i>
                   {item.product_name}
                  <span className="arrow"></span>
                 </li>
                      )
                 })
                }
                </div>
            );
}   
}            
const mapStateToProps = (state) => {//tra state return ve tu reducer ve thanh prop
    return {
      list_product_get_by_name:state.searchState.allProduct,
    };
    }
  export default connect(mapStateToProps,null)(Search);
