      callGetAllCategoryApi( error => {
      if (!error) {
        dispatch(setAllCategory(true));//tra ve cho form
      } else {
        dispatch(setAllCategory(error));//tra ve cho form
      }
    });
  }
}



function setAllCategory(allCategory) {
  return {
    type:GET_ALL_CATEGORY,
    allCategory
  }
}

function callGetAllCategoryApi( callback) {
 
    setTimeout(() => {
        axios.get(`http://127.0.0.1:3000/categories`)
           .then(res => {
             console.log('RES')
             console.log(res);
             console.log(res.data);
             if(res.data!=null) 
             {
