             }
             else return callback(new Error("Invalid any field to create a new user"))
           })
          
       }, 1000);
     }


var category_state={

    allCategory:false
}
export default function home_reducer(state =category_state, action) {
  if(action.type==' GET_ALL_CATEGORY')
  {
      let newState={...state};
      newState.allCategory=action.allCategory
      return newState;
  }
 
      return state;
  
}
