import React from 'react';
import Dashboard from './Dashboard';

class AddUser extends React.Component  {
  render(){
    let {user_name,email,password,password_confirm,address}= this.state;
    let {isSignupPending,isSignupSuccess,signupError}=this.props;
    if(isSignupSuccess==true) alert("signup successfully");
      return (
        <div>
        <Dashboard></Dashboard>
        <div style={{marginLeft:"200px"}} >
         <div style={{paddingTop:"10px",marginLeft:"50px"}}>
           <h3 className="textTTCN" style={{marginLeft:"30px"}}>User Information</h3>
           <hr style={{width:"800px",marginLeft:"30px",marginTop:"10px",backgroundColor:"#000000"}}></hr>
         </div>
         <div className="container">
             <div className="col-xs-9 col-sm-9 col-md-9 col-lg-9">
               
               <div className="col-xs-2 col-sm-2 col-md-2 col-lg-2">
               <img src={require("./chonglee.jpg")} alt="Avatar" className="avatar"/>
                <a href="#"> Change avatar</a>
               </div>
               
               <div>
                   <div className="col-xs-5 col-sm-5 col-md-5 col-lg-5" style={{paddingLeft:"20px",marginTop:"10px"}}>
                       <div className="form-group">
                         <label >Username: </label>
                         <input type="text" className="form-control" style={{paddingRight:"10px"}} name="Username"  placeholder="Username"  onChange={e => this.setState({user_name: e.target.value})} value={user_name} />
                       </div>
                       <div className="form-group">
                         <label >Email: </label>
                         <input type="text" className="form-control" name="Email" placeholder="Email"   onChange={e => this.setState({email: e.target.value})} value={email}/>
                       </div>
                       <div className="form-group">
                         <label >Address: </label>
                         <input type="text" className="form-control" name="Address" placeholder="Address"  onChange={e => this.setState({address: e.target.value})} value={address} />
                       </div>
                       <div className="form-group">
                         <label >Password: </label>
                         <input type="password" className="form-control" name="Password" placeholder="Password" onChange={e => this.setState({password: e.target.value})} value={password} />
                       </div>
                       <div className="form-group">
                         <label >Password Confirmation: </label>
                         <input type="password" className="form-control" name="Password_Confimation" placeholder="password confirm"  onChange={e => this.setState({password_confirm: e.target.value})} value={password_confirm}  />
                       </div>
                           
                       <button  style={{paddingLeft:"2px",backgroundColor:"brown",marginLeft:"150px",marginTop:"23px"}} type="submit" className="btn btn-default" onClick={e=>{this.props.signup(user_name,email,password,password_confirm,address)}}>Add User</button>
                   </div>
                 
                   </div>
                 
                  <div >
                <div>
                   
               </div>
             </div>
            </div>
           </div>
      </div>
      </div>
  );
}
}
const mapStateToProps = (state) => {//tra state return ve tu reducer ve thanh prop
  return {
    isSignupPending: state.signupState.isSignupPending,
    isSignupSuccess: state.signupState.isSignupSuccess,
    signupError: state.signupState.signupError
  };
  }
  
  const mapDispatchToProps = (dispatch) => {//store.dispatch(action)
  return {
    signup: (user_name,email,password,password_confirm,address) => dispatch(signup(user_name,email, password,password_confirm,address))//action la login voi 2 tham so la email va password
  };
  }
  export default connect(mapStateToProps, mapDispatchToProps)(Signup);
