import React, { Component } from 'react';
import { Link ,Redirect} from "react-router-dom";
import { connect } from 'react-redux';
import { show} from '../../redux/product_detail_reducer';
import './Search.css';
class Search extends Component {
  constructor(props)
  {
    super(props);
    this.state = {};
    
  }
  showDetail=id=> 
{
  console.log('true')
   let { product_id} = id;//lay trang thai off state do qua email va password
   this.props.show(product_id);
 
}
    render() {
        let { list_product_get_by_name} = this.props;
        let { product_id}=this.state;
        console.log(this)
        console.log(this.props);
        return (
                <div>
                    <div className="category-featured digital">
                     <nav className="navbar nav-menu result-brand">
                      <div className="navbar-brand">
                      <span>
                      Kết Quả Tìm Kiếm
                    </span>
                
                   </div>
                </nav>
               <div className="product-featured clearfix">
               <div className="row"></div>
               <div className="col-sm-12 category-product"></div>
                 {   
                  this.props.list_product_get_by_name.map(function(item, i){
                      let a =String(item.product_image);
                      return(
                       
                        <div className="col-sm-4">
                        <div className="product-item">
                          <div className="pi-img-wrapper">

                          <img className="img-responsive"  src={require('../../assets/'+item.product_image)} alt=""/>
                            <div>
                             <button className="btn" onClick={this.showDetail(item.id)}><Link to='./product_detail'>Detail of Product</Link></button>
                            </div>
                          </div>
                          <h3>
                          <Link to='./login'>{item.product_name}</Link>
                          </h3>
                          <div className="pi-price">
                           Price:{item.price}
                          </div>
                           <div className="pi-quantity"> 
                           <input type="number" name="quantity" min="1" max={item.quantity} />
                            </div> 
                            <div className="sticker sticker-new">
                          </div>
                            <div>
                             <button className="btn add2cart"> Add into cart</button>
                            
                            </div>
                        </div>
                      </div>


                      )
                 })
                }
                 </div>
               </div>
   
            </div>
          

                
            );
}   

}
const mapDispatchToProps = (dispatch) => {//store.dispatch(action)
return {
  show: (product_id) => dispatch(show(product_id))//action la login voi 2 tham so la email va password
};
}     
const mapStateToProps = (state) => {//tra state return ve tu reducer ve thanh prop
    return {
      list_product_get_by_name:state.searchState.allProduct,
    };
    }

  export default connect(mapStateToProps,mapDispatchToProps)(Search);
